CREATE OR REPLACE PROCEDURE permite_enc_atend_enferm_cli (nr_atendimento_p bigint) AS $body$
DECLARE

 
ie_clinica_w varchar(1);


BEGIN 
 
select	coalesce(max('S'),'N') 
into STRICT	ie_clinica_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p 
and	(ie_clinica IS NOT NULL AND ie_clinica::text <> '');
 
 
if (ie_clinica_w = 'N') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282233);
end if;
 
end;
