CREATE OR REPLACE PROCEDURE permite_lancto_apos_alta ( cd_estabelecimento_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, nr_atendimento_p bigint, ie_permite_p INOUT text) AS $body$
DECLARE

 
qt_tempo_apos_alta_w	bigint;
qt_existe_w		integer;
qt_minuto_w		bigint;
ie_permite_w		varchar(1);
cd_convenio_w		integer;
ie_tipo_atendimento_w	smallint;
cd_motivo_alta_w	smallint;

C01 CURSOR FOR 
	SELECT	qt_minuto 
	from	regra_pos_alta 
	where 	ie_situacao		= 'A' 
	and	cd_estabelecimento	= cd_estabelecimento_p 
	and	coalesce(cd_perfil, cd_perfil_p)				= cd_perfil_p 
	and	coalesce(cd_setor_atendimento, cd_setor_atendimento_p)	= cd_setor_atendimento_p 
	and	coalesce(cd_convenio, cd_convenio_w)				= cd_convenio_w 
	and	coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w)		= ie_tipo_atendimento_w 
	and 	coalesce(cd_motivo_alta, coalesce(cd_motivo_alta_w,0))		= coalesce(cd_motivo_alta_w,0) 
	order by 
		coalesce(ie_tipo_atendimento, 0), 
		coalesce(cd_convenio, 0), 
		coalesce(cd_setor_atendimento, 0), 
		coalesce(cd_perfil, 0), 
		coalesce(cd_estabelecimento, 0), 
		coalesce(cd_motivo_alta,0);


BEGIN 
ie_permite_w		:= 'S';
 
select	count(*) 
into STRICT	qt_existe_w 
from	regra_pos_alta 
where	ie_situacao = 'A';
if (qt_existe_w > 0) then 
	begin 
	select	obter_convenio_atendimento(nr_atendimento), 
		obter_tipo_atendimento(nr_atendimento), 
		trunc((clock_timestamp() - dt_alta) * 1440), 
		cd_motivo_alta 
	into STRICT	cd_convenio_w, 
		ie_tipo_atendimento_w, 
		qt_tempo_apos_alta_w, 
		cd_motivo_alta_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	open c01;
	loop 
	fetch c01 into 
		qt_minuto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		ie_permite_w	:= 'S';
		if (qt_tempo_apos_alta_w > qt_minuto_w) then 
			ie_permite_w	:= 'N';
		end if;
		end;
	end loop;
	close c01;
	end;
end if;
 
ie_permite_p	:= ie_permite_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
