CREATE OR REPLACE PROCEDURE persist_patient_insurance ( cd_departamento_p bigint, cd_pessoa_fisica_p bigint, cd_seguro_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_seguro_w     bigint := 0;

BEGIN
select  count(cd_seguro)
into STRICT    qt_seguro_w
from    seguro_padrao
where   cd_pessoa_fisica    = cd_pessoa_fisica_p
and     cd_departamento_med  = cd_departamento_p;
if (qt_seguro_w > 0) then
    update  seguro_padrao
    set     cd_seguro       = cd_seguro_p,
            dt_atualizacao  = clock_timestamp(),
            nm_usuario      = nm_usuario_p
    where   cd_pessoa_fisica     = cd_pessoa_fisica_p
    and     cd_departamento_med  = cd_departamento_p;
else
    insert into seguro_padrao(
                    nr_sequencia,
                    dt_atualizacao,
                    nm_usuario,
                    dt_atualizacao_nrec,
                    nm_usuario_nrec,
                    cd_seguro,
                    cd_pessoa_fisica,
                    cd_departamento_med)
        values (
                    nextval('seguro_padrao_seq'),
                    clock_timestamp(),
                    nm_usuario_p,
                    clock_timestamp(),
                    nm_usuario_p,
                    cd_seguro_p,
                    cd_pessoa_fisica_p,
                    cd_departamento_p);
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
