SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pfcs_activate_records_covid ( nr_seq_tactical_level_p bigint) AS $body$
DECLARE


qt_reg_w	bigint := 0;


BEGIN

select	count(1)
into STRICT	qt_reg_w
from	pfcs_panel_detail
where	nr_seq_operational_level in (	SELECT	x.nr_sequencia
					from	pfcs_operational_level x
					where	x.nr_seq_tactical_level = nr_seq_tactical_level_p)
and	ie_situation = 'A';

if (qt_reg_w > 0) then

	delete	FROM pfcs_panel_detail
	where	nr_seq_operational_level in (	SELECT	x.nr_sequencia
						from	pfcs_operational_level x
						where	x.nr_seq_tactical_level = nr_seq_tactical_level_p)
	and	ie_situation = 'A';

end if;

update	pfcs_panel_detail
set	ie_situation	= 'A',
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= 'Philips'
where	nr_seq_operational_level in (	SELECT	x.nr_sequencia
					from	pfcs_operational_level x
					where	x.nr_seq_tactical_level = nr_seq_tactical_level_p)
and	ie_situation = 'T';

select	count(1)
into STRICT	qt_reg_w
from	pfcs_panel
where	nr_seq_operational_level in (	SELECT	x.nr_sequencia
					from	pfcs_operational_level x
					where	x.nr_seq_tactical_level = nr_seq_tactical_level_p)
and	ie_situation = 'A';

if (qt_reg_w > 0) then

	delete	FROM pfcs_panel
	where	nr_seq_operational_level in (	SELECT	x.nr_sequencia
						from	pfcs_operational_level x
						where	x.nr_seq_tactical_level = nr_seq_tactical_level_p)
	and	ie_situation = 'A';

end if;

update	pfcs_panel
set	ie_situation	= 'A',
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= 'Philips'
where	nr_seq_operational_level in (	SELECT	x.nr_sequencia
					from	pfcs_operational_level x
					where	x.nr_seq_tactical_level = nr_seq_tactical_level_p)
and	ie_situation = 'T';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
