SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pfcs_generate_registers ( nr_seq_indicator_p bigint, cd_establishment_p bigint default null) AS $body$
DECLARE


    cur_establishments CURSOR FOR
    SELECT e.cd_estabelecimento cd_establishment
    from estabelecimento e,
        empresa p
    where e.ie_situacao = PFCS_PCK_CONSTANTS.IE_ACTIVE
        and p.ie_situacao = PFCS_PCK_CONSTANTS.IE_ACTIVE
        and e.nm_usuario_nrec = PFCS_PCK_CONSTANTS.NM_USER_PFCS
        and e.cd_empresa = p.cd_empresa
        and e.cd_estabelecimento = coalesce(cd_establishment_p,e.cd_estabelecimento);

    ds_procedure_w  PFCS_PCK_SUBTYPES.VARCHAR_255_T;
BEGIN
    if (nr_seq_indicator_p IS NOT NULL AND nr_seq_indicator_p::text <> '') then
        select  coalesce(max(a.ds_procedure),max(b.ds_procedure)) ds_procedure
        into STRICT    ds_procedure_w
        FROM pfcs_indicator a
LEFT OUTER JOIN pfcs_indicator b ON (a.nr_seq_main_indicator = b.nr_sequencia)
WHERE a.nr_sequencia = nr_seq_indicator_p;

        CALL philips_param_pck.set_nr_seq_idioma(coalesce(wheb_usuario_pck.get_nr_seq_idioma,5));
        if (ds_procedure_w IS NOT NULL AND ds_procedure_w::text <> '') then
            for c01_w in cur_establishments loop
                EXECUTE
                'begin ' ||
                    ds_procedure_w||'('||c01_w.cd_establishment||','||chr(39)||PFCS_PCK_CONSTANTS.NM_USER_PFCS||chr(39)||'); ' ||
                'end;';
            end loop;
        end if;
    end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
