SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pfcs_hospitalization_avg_time ( nr_seq_indicator_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


-- Tasy cursors
c01_tasy CURSOR FOR
SELECT	a.nr_atendimento nr_encounter,
    a.cd_pessoa_fisica id_patient,
    coalesce(get_formatted_person_name(a.cd_pessoa_fisica, 'list'), obter_nome_pf(a.cd_pessoa_fisica)) nm_patient,
    pfcs_obter_lista_dados_classif(a.cd_pessoa_fisica) ds_classification,
    obter_sexo_pf(a.cd_pessoa_fisica, 'D') ds_gender,
    pf.dt_nascimento dt_birthdate,
    obter_dados_pf(a.cd_pessoa_fisica, 'I') qt_idade_paciente,
    a.dt_entrada dt_entry_date,
    round((clock_timestamp() - a.dt_entrada) * 1440) qt_time_hospitalization,
    d.dt_entrada_unidade dt_entry_unit,
    b.cd_unidade_basica || ' ' || b.cd_unidade_compl ds_location,
    substr(obter_valor_dominio(82, b.ie_status_unidade),1,255) ds_status,
    b.ie_status_unidade ie_status,
    c.cd_setor_atendimento cd_department,
    c.ds_setor_atendimento ds_department
from	atendimento_paciente a,
    unidade_atendimento b,
    setor_atendimento c,
    atend_paciente_unidade d,
    pessoa_fisica pf
where a.nr_atendimento = d.nr_atendimento
    and b.cd_setor_atendimento = c.cd_setor_atendimento
    and b.cd_setor_atendimento = d.cd_setor_atendimento
    and b.cd_unidade_basica = d.cd_unidade_basica
    and b.cd_unidade_compl = d.cd_unidade_compl
    and d.nr_seq_interno = obter_atepacu_paciente(b.nr_atendimento,'A')
    and a.cd_pessoa_fisica = pf.cd_pessoa_fisica
    and b.ie_situacao = 'A'
    and c.ie_situacao = 'A'
    and c.cd_classif_setor in ('1','3','4','9','11','12')
    and c.ie_ocup_hospitalar <> 'N'
    and c.cd_estabelecimento_base = cd_estabelecimento_p
    and coalesce(a.dt_alta::text, '') = ''
    and coalesce(a.dt_cancelamento::text, '') = '';


-- Integration cursors
c01_fhir CURSOR FOR
SELECT enc.id_encounter nr_encounter,
    pat.patient_id id_patient,
    pfcs_get_human_name(pat.nr_sequencia, 'Patient') nm_patient,
    substr(enc.ds_classification,1,255) ds_classification,
    pat.gender ds_gender,
    pat.birthdate dt_birthdate,
    trunc(months_between(coalesce(pat.deceased_date, clock_timestamp()), pat.birthdate)/12) qt_idade_paciente,
    enc.period_start dt_entry_date,
    round((clock_timestamp() - enc.period_start) * 1440) qt_time_hospitalization,
    uni.dt_entrada_unidade dt_entry_unit,
    uni.cd_unidade_basica || ' ' || uni.cd_unidade_compl ds_location,
    substr(obter_valor_dominio(82, uni.ie_status_unidade),1,255) ds_status,
    uni.ie_status_unidade ie_status,
    sec.cd_setor_atendimento cd_department,
    sec.ds_setor_atendimento ds_department
from pfcs_encounter enc,
    pfcs_patient pat,
    unidade_atendimento uni,
    setor_atendimento sec
where enc.nr_seq_patient = pat.nr_sequencia
    and uni.nr_seq_location = pfcs_get_pat_location(pat.nr_sequencia, enc.nr_sequencia)
    and uni.cd_setor_atendimento = sec.cd_setor_atendimento
    and sec.ie_situacao = 'A'
    and uni.ie_situacao = 'A'
    and sec.cd_classif_setor in ('1','3','4','9','11','12')
    and sec.cd_estabelecimento = cd_estabelecimento_p
    and (enc.period_start IS NOT NULL AND enc.period_start::text <> '')
    and coalesce(enc.period_end::text, '') = '';

-- Variables
pfcs_flag_settings_w            pfcs_general_rule.ie_table_origin%type;
nr_seq_operational_level_w		pfcs_operational_level.nr_sequencia%type;
pfcs_panel_detail_seq_w			pfcs_panel_detail.nr_sequencia%type;
nr_seq_panel_w				    pfcs_panel_detail.nr_seq_panel%type;
qt_total_w				        bigint := 0;
qt_total_time_patient_w			bigint := 0;
BEGIN
nr_seq_operational_level_w := pfcs_get_structure_level(
        cd_establishment_p => cd_estabelecimento_p,
        ie_level_p => 'O',
        ie_info_p => 'C');

select ie_table_origin
into STRICT pfcs_flag_settings_w
from pfcs_general_rule;


-- Tasy tables or both
if (pfcs_flag_settings_w = 0 or pfcs_flag_settings_w = 2) then
    for c01_w in c01_tasy loop
		if (c01_w.qt_time_hospitalization > 0) then
            qt_total_w := qt_total_w + 1;
			qt_total_time_patient_w	:= qt_total_time_patient_w + c01_w.qt_time_hospitalization;

			select	nextval('pfcs_panel_detail_seq')
			into STRICT	pfcs_panel_detail_seq_w
			;

			insert into pfcs_panel_detail(
				nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				ie_situation,
				nr_seq_indicator,
				nr_seq_operational_level)
			values (
				pfcs_panel_detail_seq_w,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				'T',
				nr_seq_indicator_p,
				nr_seq_operational_level_w);

			insert into pfcs_detail_patient(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_encounter_varchar,
				id_patient,
				nm_patient,
				ds_classification,
				ds_gender,
				dt_birthdate,
                ds_age_range,
				dt_entrance,
				qt_time_total_hospitalization,
				nr_seq_detail)
			values (
				nextval('pfcs_detail_patient_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				c01_w.nr_encounter,
				c01_w.id_patient,
				c01_w.nm_patient,
				c01_w.ds_classification,
				c01_w.ds_gender,
				c01_w.dt_birthdate,
                c01_w.qt_idade_paciente,
				c01_w.dt_entry_date,
				c01_w.qt_time_hospitalization,
				pfcs_panel_detail_seq_w);


			insert into pfcs_detail_bed(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_department,
				ds_department,
				dt_entry_unit,
				ds_location,
				ds_status,
				ie_status,
				nr_seq_detail)
			values (
				nextval('pfcs_detail_bed_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				c01_w.cd_department,
				c01_w.ds_department,
				c01_w.dt_entry_unit,
				c01_w.ds_location,
				c01_w.ds_status,
				c01_w.ie_status,
				pfcs_panel_detail_seq_w);
		end if;
		commit;
	end loop;
end if;

-- Integration tables or both
if (pfcs_flag_settings_w = 1 or pfcs_flag_settings_w = 2) then
    for c01_w in c01_fhir loop
		if (c01_w.qt_time_hospitalization > 0) then
            qt_total_w := qt_total_w + 1;
			qt_total_time_patient_w	:= qt_total_time_patient_w + c01_w.qt_time_hospitalization;

			select	nextval('pfcs_panel_detail_seq')
			into STRICT	pfcs_panel_detail_seq_w
			;

			insert into pfcs_panel_detail(
				nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				ie_situation,
				nr_seq_indicator,
				nr_seq_operational_level)
			values (
				pfcs_panel_detail_seq_w,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				'T',
				nr_seq_indicator_p,
				nr_seq_operational_level_w);

			insert into pfcs_detail_patient(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_encounter_varchar,
				id_patient,
				nm_patient,
				ds_classification,
				ds_gender,
				dt_birthdate,
                ds_age_range,
				dt_entrance,
				qt_time_total_hospitalization,
				nr_seq_detail)
			values (
				nextval('pfcs_detail_patient_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				c01_w.nr_encounter,
				c01_w.id_patient,
				c01_w.nm_patient,
				c01_w.ds_classification,
				c01_w.ds_gender,
				c01_w.dt_birthdate,
                c01_w.qt_idade_paciente,
				c01_w.dt_entry_date,
				c01_w.qt_time_hospitalization,
				pfcs_panel_detail_seq_w);


			insert into pfcs_detail_bed(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_department,
				ds_department,
				dt_entry_unit,
				ds_location,
				ds_status,
				ie_status,
				nr_seq_detail)
			values (
				nextval('pfcs_detail_bed_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				c01_w.cd_department,
				c01_w.ds_department,
				c01_w.dt_entry_unit,
				c01_w.ds_location,
				c01_w.ds_status,
				c01_w.ie_status,
				pfcs_panel_detail_seq_w);
		end if;
		commit;
	end loop;
end if;

 := pfcs_pck.pfcs_generate_results(
		vl_indicator_p => qt_total_time_patient_w, vl_indicator_aux_p => qt_total_w, nr_seq_indicator_p => nr_seq_indicator_p, nr_seq_operational_level_p => nr_seq_operational_level_w, nm_usuario_p => nm_usuario_p, nr_seq_panel_p => nr_seq_panel_w);

CALL pfcs_pck.pfcs_update_detail(
		nr_seq_indicator_p => nr_seq_indicator_p,
		nr_seq_panel_p => nr_seq_panel_w,
		nr_seq_operational_level_p => nr_seq_operational_level_w,
		nm_usuario_p => nm_usuario_p);

CALL pfcs_pck.pfcs_activate_records(
		nr_seq_indicator_p => nr_seq_indicator_p,
		nr_seq_operational_level_p => nr_seq_operational_level_w,
		nm_usuario_p => nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pfcs_hospitalization_avg_time ( nr_seq_indicator_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

