SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pfcs_hospitalization_rate ( nr_seq_indicator_p bigint, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE

	
c01 CURSOR FOR
	SELECT	p.cd_pessoa_fisica id_patient,
		coalesce(get_formatted_person_name(p.cd_pessoa_fisica, 'list'), obter_nome_pf(p.cd_pessoa_fisica)) nm_patient,
		pfcs_obter_lista_dados_classif(p.cd_pessoa_fisica) ds_classification,
		obter_sexo_pf(p.cd_pessoa_fisica, 'D') ds_gender,
		pf.dt_nascimento dt_birthdate,
        obter_dados_pf(p.cd_pessoa_fisica, 'I') qt_idade_paciente,
		p.nr_atendimento nr_encounter,
		p.dt_entrada dt_entrance
	from	atendimento_paciente p, pessoa_fisica pf
	where	to_date(obter_dados_desfecho_pa(p.nr_atendimento, 'UP'), 'dd/mm/yyyy hh24:mi:ss') > pfcs_get_pa_period
	and (p.ie_tipo_atendimento = 3 or (p.ie_tipo_atendimento = 1 and ((p.dt_usuario_intern IS NOT NULL AND p.dt_usuario_intern::text <> '') or (p.nr_atend_origem_pa IS NOT NULL AND p.nr_atend_origem_pa::text <> ''))))
	and	coalesce(p.dt_cancelamento::text, '') = ''
	and	p.cd_estabelecimento = (cd_estabelecimento_p)::numeric
	and	p.cd_pessoa_fisica = pf.cd_pessoa_fisica;

c02 CURSOR(nr_atendimento_p bigint) FOR
	SELECT	1/*decode(nvl(count(*),0),0,'N','S') ie_hospitalized*/
	from	atendimento_alta a, parametro_medico pm
	where	a.nr_atendimento = nr_atendimento_p
	and	a.ie_desfecho = 'I'
	and     pm.cd_estabelecimento = (cd_estabelecimento_p)::numeric
	and 	((a.ie_tipo_orientacao <> 'P')
	or (coalesce(pm.ie_liberar_desfecho,'N')  = 'N')
	or  	((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = '')));

nr_seq_operational_level_w		pfcs_operational_level.nr_sequencia%type;
pfcs_panel_detail_seq_w			pfcs_panel_detail.nr_sequencia%type;
nr_seq_panel_w				pfcs_panel_detail.nr_seq_panel%type;
qt_total_w				bigint := 0;
qt_hospitalization_w			bigint := 0;
BEGIN

	nr_seq_operational_level_w := pfcs_get_structure_level(
									cd_establishment_p => cd_estabelecimento_p,
									ie_level_p => 'O',
									ie_info_p => 'C');

	for c01_w in c01 loop
		begin

			qt_total_w := qt_total_w + 1;

			for c02_w in c02(c01_w.nr_encounter) loop
				begin

					qt_hospitalization_w := qt_hospitalization_w + 1;

					select	nextval('pfcs_panel_detail_seq')
					into STRICT	pfcs_panel_detail_seq_w
					;

					insert into pfcs_panel_detail(
						nr_sequencia,
						nm_usuario,
						dt_atualizacao,
						nm_usuario_nrec,
						dt_atualizacao_nrec,
						ie_situation,
						nr_seq_indicator,
						nr_seq_operational_level)
					values (
						pfcs_panel_detail_seq_w,
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						'T',
						nr_seq_indicator_p,
						nr_seq_operational_level_w);

					insert into pfcs_detail_patient(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_encounter,
						dt_entrance,
						id_patient,
						nm_patient,
						ds_gender,
						ds_classification,
						dt_birthdate,
                        ds_age_range,
						nr_seq_detail)
					values (
						nextval('pfcs_detail_patient_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						c01_w.nr_encounter,
						c01_w.dt_entrance,
						c01_w.id_patient,
						c01_w.nm_patient,
						c01_w.ds_gender,
						c01_w.ds_classification,
						c01_w.dt_birthdate,
                        c01_w.qt_idade_paciente,
						pfcs_panel_detail_seq_w);

				end;
			end loop;

			commit;

		end;
	end loop;

	 := pfcs_pck.pfcs_generate_results(
		vl_indicator_p => qt_hospitalization_w, vl_indicator_aux_p => qt_total_w, nr_seq_indicator_p => nr_seq_indicator_p, nr_seq_operational_level_p => nr_seq_operational_level_w, nm_usuario_p => nm_usuario_p, nr_seq_panel_p => nr_seq_panel_w);

	CALL pfcs_pck.pfcs_update_detail(
		nr_seq_indicator_p => nr_seq_indicator_p,
		nr_seq_panel_p => nr_seq_panel_w,
		nr_seq_operational_level_p => nr_seq_operational_level_w,
		nm_usuario_p => nm_usuario_p);

	CALL pfcs_pck.pfcs_activate_records(
		nr_seq_indicator_p => nr_seq_indicator_p,
		nr_seq_operational_level_p => nr_seq_operational_level_w,
		nm_usuario_p => nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pfcs_hospitalization_rate ( nr_seq_indicator_p bigint, cd_estabelecimento_p text, nm_usuario_p text) FROM PUBLIC;

