CREATE OR REPLACE PROCEDURE pfcs_int_empresa ( cd_empresa_p text, nm_razao_social_p text, ie_situacao_p text, nm_usuario_p text) AS $body$
DECLARE


json_w        		 philips_json;
json_data_w   		 text;
param_version_pfcs_w funcao_param_Usuario.vl_parametro%type;	


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '' AND cd_empresa_p IS NOT NULL AND cd_empresa_p::text <> '')then

	param_version_pfcs_w := obter_param_usuario(9041, 14, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, param_version_pfcs_w);

	json_w := philips_json();
	json_w.put('DATA_ATUAL', TO_CHAR(clock_timestamp(),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('CD_EMPRESA', cd_empresa_p);
	json_w.put('NOME_EMPRESA', nm_razao_social_p);
	json_w.put('IE_SITUACAO', ie_situacao_p);
	json_w.put('versionID', coalesce(param_version_pfcs_w,'1.2'));
	json_w.put('orgid', cd_empresa_p);
	dbms_lob.createtemporary(json_data_w, true);
	json_w.(json_data_w);

	json_data_w := bifrost.send_integration_content('PFCS.company.event', json_data_w, nm_usuario_p);
		
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
