CREATE OR REPLACE PROCEDURE pfcs_int_link_device ( nr_sequencia_device_p bigint, ie_situacao_p text, nm_device_p text, nm_setor_leito_p text, cd_cgc_p text, cd_pessoa_fisica_p text, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


json_w        		 philips_json;
json_data_w   		 text;
param_version_pfcs_w funcao_param_usuario.vl_parametro%type;


BEGIN
if (coalesce(nr_sequencia_device_p,0) > 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	
	param_version_pfcs_w := obter_param_usuario(9041, 14, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, param_version_pfcs_w);

	json_w := philips_json();
	json_w.put('DATA_ATUAL', 		TO_CHAR(clock_timestamp(),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('NR_SEQUENCIA_DEVICE',	nr_sequencia_device_p);	
	json_w.put('IE_SITUACAO_DEVICE',	ie_situacao_p);
	json_w.put('NM_DEVICE',				nm_device_p);
	json_w.put('NM_SETOR_LEITO',		nm_setor_leito_p);
	json_w.put('CD_ESTABELECIMENTO',	cd_cgc_p);
	json_w.put('CD_PESSOA_FISICA',		coalesce(cd_pessoa_fisica_p, 'null'));
	json_w.put('versionID', 			coalesce(param_version_pfcs_w,'1.2'));
	json_w.put('orgid', cd_cgc_p);
	dbms_lob.createtemporary(json_data_w, true);
	json_w.(json_data_w);
		
	json_data_w := bifrost.send_integration_content('PFCS.device.event', json_data_w, nm_usuario_p);	
end if;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
