SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pfcs_int_patient ( cd_pessoa_fisica_p text, cd_cgc_p text, nm_usuario_p text) AS $body$
DECLARE


json_w        		philips_json;
json_data_w   		text;
nm_pessoa_fisica_w	pessoa_fisica.nm_pessoa_fisica%type;
ie_sexo_w			pessoa_fisica.nm_pessoa_fisica%type;
dt_nascimento_w 	pessoa_fisica.nm_pessoa_fisica%type;
cd_estado_civil_w	pessoa_fisica.cd_pessoa_fisica%type;
ds_estado_civil_w 	pessoa_fisica.nm_pessoa_fisica%type;
cd_cgc_w			estabelecimento.cd_cgc%type;
param_version_pfcs_w 	funcao_param_usuario.vl_parametro%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	Select 	obter_nome_pf(p.cd_pessoa_fisica) nm_pessoa_fisica,
			CASE WHEN p.ie_sexo='M' THEN 'male' WHEN p.ie_sexo='F' THEN 'female'  ELSE 'other' END  ie_sexo,
			TO_CHAR(p.dt_nascimento,'YYYY-MM-DD"T"HH24:MI:SS".000Z"') dt_nascimento,
			CASE WHEN p.ie_estado_civil='1' THEN 'U' WHEN p.ie_estado_civil='2' THEN 'M' WHEN p.ie_estado_civil='3' THEN 'D' WHEN p.ie_estado_civil='4' THEN 'L' WHEN p.ie_estado_civil='5' THEN 'W' WHEN p.ie_estado_civil='6' THEN 'L' WHEN p.ie_estado_civil='7' THEN 'C' WHEN p.ie_estado_civil='9' THEN 'U' END  cd_estado_civil,
			CASE WHEN p.ie_estado_civil='1' THEN 'Unmarried' WHEN p.ie_estado_civil='2' THEN 'Married' WHEN p.ie_estado_civil='3' THEN 'Divorced' WHEN p.ie_estado_civil='4' THEN 'Separated' WHEN p.ie_estado_civil='5' THEN 'Widowed' WHEN p.ie_estado_civil='6' THEN 'Separated' WHEN p.ie_estado_civil='7' THEN 'Common Law' WHEN p.ie_estado_civil='9' THEN 'Unmarried'  END  ds_estado_civil,
			obter_estab_id_pfcs(p.cd_estabelecimento) cd_cgc
	into STRICT	nm_pessoa_fisica_w,
			ie_sexo_w,
			dt_nascimento_w,
			cd_estado_civil_w,
			ds_estado_civil_w,
			cd_cgc_w
	FROM   	pessoa_fisica p
	WHERE  	p.cd_pessoa_fisica = cd_pessoa_fisica_p;

	param_version_pfcs_w := obter_param_usuario(9041, 14, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, param_version_pfcs_w);

	json_w := philips_json();
	json_w.put('DATA_ATUAL', TO_CHAR(clock_timestamp(),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('CD_PESSOA_FISICA', cd_pessoa_fisica_p);
	json_w.put('NM_PESSOA_FISICA', nm_pessoa_fisica_w);
	json_w.put('IE_SEXO', ie_sexo_w);
	json_w.put('DT_NASCIMENTO', dt_nascimento_w);
	json_w.put('CD_ESTADO_CIVIL', cd_estado_civil_w);
	json_w.put('DS_ESTADO_CIVIL', ds_estado_civil_w);
	json_w.put('CD_ESTABELECIMENTO', coalesce(cd_cgc_p,cd_cgc_w));
	json_w.put('versionID', coalesce(param_version_pfcs_w,'1.2'));
	json_w.put('orgid', coalesce(cd_cgc_p,cd_cgc_w));
	dbms_lob.createtemporary(json_data_w, true);
	json_w.(json_data_w);

	json_data_w := bifrost.send_integration_content('PFCS.patient.event', json_data_w, nm_usuario_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
