CREATE OR REPLACE PROCEDURE pfcs_int_practitioner ( cd_medico_atendimento_p text, nr_atendimento_p bigint, cd_cgc_p text, nm_usuario_p text) AS $body$
DECLARE

				
json_w        		philips_json;
json_data_w   		text;
nome_medico_w		  pessoa_fisica.nm_pessoa_fisica%type;
param_version_pfcs_w 	funcao_param_usuario.vl_parametro%type;


BEGIN

if (coalesce(cd_medico_atendimento_p,0) > 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select 	obter_nome_medico(cd_medico_atendimento_p, 'PNGPF') nome_medico
	into STRICT 	nome_medico_w
	;
	
	param_version_pfcs_w := obter_param_usuario(9041, 14, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, param_version_pfcs_w);

	json_w := philips_json();
	json_w.put('DATA_ATUAL', TO_CHAR(clock_timestamp(),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('CD_MEDICO', cd_medico_atendimento_p);
	json_w.put('NM_MEDICO', nome_medico_w);
	json_w.put('CD_MEDICO_FUNCAO', nr_atendimento_p||cd_medico_atendimento_p);
	json_w.put('CD_ESTABELECIMENTO', cd_cgc_p);
	json_w.put('versionID', coalesce(param_version_pfcs_w,'1.2'));
	json_w.put('orgid', cd_cgc_p);
	dbms_lob.createtemporary(json_data_w, true);
	json_w.(json_data_w);
	
	json_data_w := bifrost.send_integration_content('PFCS.practitioner.event', json_data_w, nm_usuario_p);

end if;

end;
$body$
