CREATE OR REPLACE PROCEDURE pfcs_int_sinal_vital ( nr_sequencia_p bigint, ie_status_p text, cd_pessoa_fisica_p text, nr_atendimento_p bigint, dt_sinal_vital_p timestamp, qt_pa_diastolica_p bigint, qt_pa_sistolica_p bigint, qt_freq_cardiaca_p bigint, qt_freq_resp_p bigint, qt_saturacao_o2_p bigint, nm_usuario_p text) AS $body$
DECLARE


json_w        			philips_json;
json_data_w   			text;
param_version_pfcs_w	funcao_param_Usuario.vl_parametro%type;
cd_cgc_atendimento_w	pfcs_organization.id_organization%type;
cd_cgc_pessoa_w			pfcs_organization.id_organization%type;


BEGIN
if (coalesce(nr_atendimento_p,0) > 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	param_version_pfcs_w := obter_param_usuario(9041, 14, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, param_version_pfcs_w);

	select  obter_estab_id_pfcs(obter_estab_atendimento(nr_atendimento_p)),
		    obter_estab_id_pfcs((select max(p.cd_estabelecimento) from pessoa_fisica p where p.cd_pessoa_fisica = cd_pessoa_fisica_p))
	into STRICT	cd_cgc_atendimento_w,
			cd_cgc_pessoa_w
	;

	json_w := philips_json();
	json_w.put('DATA_ATUAL', 			TO_CHAR(clock_timestamp(),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('CD_PESSOA_FISICA', 			cd_pessoa_fisica_p);
	json_w.put('NR_ATENDIMENTO', 			nr_atendimento_p);

	json_w.put('NR_SEQ_SINAL_VITAL_ATENDIMENTO_1',	nr_sequencia_p||'_1');
	json_w.put('IE_STATUS_1',			ie_status_p);
	json_w.put('DT_SINAL_VITAL_1',			TO_CHAR(coalesce(dt_sinal_vital_p,clock_timestamp()),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('QT_PA_DIASTOLICA',			qt_pa_diastolica_p);

	json_w.put('NR_SEQ_SINAL_VITAL_ATENDIMENTO_2',	nr_sequencia_p||'_2');
	json_w.put('IE_STATUS_2',			ie_status_p);
	json_w.put('DT_SINAL_VITAL_2',			TO_CHAR(coalesce(dt_sinal_vital_p,clock_timestamp()),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('QT_FREQ_CARDIACA',			qt_freq_cardiaca_p);

	json_w.put('NR_SEQ_SINAL_VITAL_ATENDIMENTO_3',	nr_sequencia_p||'_3');
	json_w.put('IE_STATUS_3',			ie_status_p);
	json_w.put('DT_SINAL_VITAL_3',			TO_CHAR(coalesce(dt_sinal_vital_p,clock_timestamp()),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('QT_FREQ_RESP',			qt_freq_resp_p);

	json_w.put('NR_SEQ_SINAL_VITAL_ATENDIMENTO_4',	nr_sequencia_p||'_4');
	json_w.put('IE_STATUS_4',			ie_status_p);
	json_w.put('DT_SINAL_VITAL_4',			TO_CHAR(coalesce(dt_sinal_vital_p,clock_timestamp()),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('QT_PA_SISTOLICA',			qt_pa_sistolica_p);

	json_w.put('NR_SEQ_SINAL_VITAL_ATENDIMENTO_5',	nr_sequencia_p||'_5');
	json_w.put('IE_STATUS_5',			ie_status_p);
	json_w.put('DT_SINAL_VITAL_5',			TO_CHAR(coalesce(dt_sinal_vital_p,clock_timestamp()),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('QT_SATURACAO_O2',			qt_saturacao_o2_p);
	json_w.put('versionID', coalesce(param_version_pfcs_w,'1.2'));
	json_w.put('orgid', coalesce(cd_cgc_atendimento_w, cd_cgc_pessoa_w));
	dbms_lob.createtemporary(json_data_w, true);
	json_w.(json_data_w);

	json_data_w := bifrost.send_integration_content('PFCS.vital.signs.event', json_data_w, nm_usuario_p);
end if;

end;
$body$
