CREATE OR REPLACE PROCEDURE pfcs_int_special_request (nr_seq_special_request_p text, cd_special_request_p text, ds_special_request_p text, ie_status_p text, dt_inicio_p timestamp, cd_pessoa_fisica_p text, nr_atendimento_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


json_w        		 philips_json;
json_data_w   		 text;
param_version_pfcs_w varchar(255);
cd_cgc_atendimento_w	pfcs_organization.id_organization%type;
cd_cgc_pessoa_w			pfcs_organization.id_organization%type;


BEGIN
if (coalesce(nr_atendimento_p,0) > 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_special_request_p IS NOT NULL AND cd_special_request_p::text <> '') then

	param_version_pfcs_w := obter_param_usuario(9041, 14, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, param_version_pfcs_w);

	select  obter_estab_id_pfcs(obter_estab_atendimento(nr_atendimento_p)),
		    obter_estab_id_pfcs((select max(p.cd_estabelecimento) from pessoa_fisica p where p.cd_pessoa_fisica = cd_pessoa_fisica_p))
	into STRICT	cd_cgc_atendimento_w,
			cd_cgc_pessoa_w
	;

	json_w := philips_json();
	json_w.put('DATA_ATUAL', 					TO_CHAR(clock_timestamp(),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('CD_PESSOA_FISICA',				cd_pessoa_fisica_p);
	json_w.put('NR_ATENDIMENTO',				nr_atendimento_p);
	json_w.put('NR_SEQUENCIA_SPECIAL_REQUEST',	nr_seq_special_request_p);
	json_w.put('IE_STATUS',						ie_status_p);
	json_w.put('CD_SPECIAL_REQUEST',			cd_special_request_p);
	json_w.put('DS_SPECIAL_REQUEST',			ds_special_request_p);
	json_w.put('DT_INICIO_SPECIAL_REQUEST',		TO_CHAR(coalesce(dt_inicio_p,clock_timestamp()),'YYYY-MM-DD"T"HH24:MI:SS".000Z"'));
	json_w.put('versionID', coalesce(param_version_pfcs_w,'1.2'));
	json_w.put('orgid', coalesce(cd_cgc_atendimento_w, cd_cgc_pessoa_w));
	dbms_lob.createtemporary(json_data_w, true);
	json_w.(json_data_w);

	json_data_w := bifrost.send_integration_content('PFCS.special.request.event', json_data_w, nm_usuario_p);

end if;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
