SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pfcs_pa_admits_to_or ( cd_establishment_p bigint, nm_user_p text) AS $body$
DECLARE


nr_seq_indicator_w          pfcs_panel.nr_seq_indicator%type := PFCS_PCK_INDICATORS.NR_ED_ADMITS_FROM_OR;
pfcs_panel_detail_seq_w     pfcs_panel_detail.nr_sequencia%type;
pfcs_panel_seq_w            pfcs_panel.nr_sequencia%type;


qt_register_w    numeric(20) := 0;
qt_total_time_w  numeric(20) := 0;

c01 CURSOR FOR
SELECT enc.id_encounter nr_encounter,
    pfcs_get_human_name(pat.nr_sequencia, PFCS_PCK_CONSTANTS.DS_PATIENT) nm_patient,
    pat.patient_id id_patient,
    pat.birthdate dt_birthdate,
    round(months_between(coalesce(pat.deceased_date, clock_timestamp()), pat.birthdate)) qt_patient_age,
    pat.gender ds_gender,
    pfcs_pck_utils.get_full_location(cur_uni.nr_seq_location) ds_current_location,
    pfcs_pck_utils.get_full_location(req_uni.nr_seq_location) ds_requested_location,
    enc.period_start dt_period_start,
    pfcs_get_patient_diagnosis(enc.nr_sequencia) ds_problem,
    coalesce(psr.dt_authored_on, psr.dt_atualizacao) dt_request,
    pfcs_get_human_name(pfcs_get_practitioner_seq(enc.nr_sequencia, PFCS_PCK_CONSTANTS.CD_ATTENDING_PHYSICIAN), PFCS_PCK_CONSTANTS.DS_PRACTITIONER) nm_practioner,
    pfcs_get_edi_score(enc.nr_sequencia) qt_trs,
    pfcs_obs_contributor_pck.get_edi_vital_warnings(enc.nr_sequencia) ds_edi_vitals_warn,
    pfcs_obs_contributor_pck.get_edi_contributors(enc.nr_sequencia) ds_edi_contrb,
    pfcs_get_code_status(pat.nr_sequencia, enc.nr_sequencia, PFCS_PCK_CONSTANTS.IE_YES_BR) cd_code_status,
    pfcs_get_special_requests(enc.nr_sequencia, cur_uni.nr_seq_location,PFCS_PCK_CONSTANTS.IE_SPECIAL_REQ_ENCOUNTER) ds_special_requests,
	coalesce(pfcs_get_checklist(enc.nr_sequencia, pat.nr_sequencia, PFCS_PCK_CONSTANTS.CD_MPL),null) ds_care_status,
    coalesce(pfcs_get_checklist(enc.nr_sequencia, pat.nr_sequencia, PFCS_PCK_CONSTANTS.CD_TOOLTIP),null) ds_checklist,
    pfcs_get_frequent_flyer(enc.nr_sequencia) ie_frequent_flyer,
    pfcs_get_lace_plus(enc.nr_sequencia, enc.nr_seq_organization) ds_readmission_risk,
    pfcs_obs_contributor_pck.get_laceplus(enc.nr_sequencia) ds_readm_risk_contrb,
	pfcs_obs_contributor_pck.get_freqflyer_visits(enc.nr_sequencia) ds_recur_pat_adm_data,
	pfcs_obs_contributor_pck.get_freqflyer_comorbidities(enc.nr_sequencia) ds_recur_pat_comorbd,
	pfcs_obs_contributor_pck.get_freqflyer_visit_reason(enc.nr_sequencia) ds_recur_pat_reasons,
    cur_uni.ie_status_unidade cd_status,
    cur_sec.cd_setor_atendimento cd_unit,
    cur_sec.ds_setor_atendimento ds_department
from pfcs_service_request psr,
    pfcs_encounter enc,
    pfcs_patient pat,
    unidade_atendimento cur_uni,
    setor_atendimento cur_sec,
    unidade_atendimento req_uni,
    setor_atendimento req_sec
where enc.nr_seq_patient = pat.nr_sequencia
    and psr.nr_seq_encounter = enc.nr_sequencia
    and psr.nr_seq_location <> cur_uni.nr_seq_location
    and psr.nr_seq_location = req_uni.nr_seq_location
    and cur_uni.nr_seq_location =  coalesce(enc.nr_seq_location, pfcs_get_pat_location(pat.nr_sequencia, enc.nr_sequencia))
    and cur_sec.cd_setor_atendimento = cur_uni.cd_setor_atendimento
    and req_sec.cd_setor_atendimento = req_uni.cd_setor_atendimento
    and (req_sec.cd_classif_setor in (PFCS_PCK_CONSTANTS.CD_ACU,PFCS_PCK_CONSTANTS.CD_ICU,PFCS_PCK_CONSTANTS.CD_NEWBORN,PFCS_PCK_CONSTANTS.CD_POST_DISCH_WAITING_ROOM,PFCS_PCK_CONSTANTS.CD_WAITING_ROOM)
        or psr.ds_service = PFCS_PCK_CONSTANTS.DS_ADMIT_INPATIENT)
    and cur_sec.cd_classif_setor = PFCS_PCK_CONSTANTS.CD_OR
    and (enc.period_start IS NOT NULL AND enc.period_start::text <> '')
    and coalesce(enc.period_end::text, '') = ''
    and upper(psr.cd_service) = PFCS_PCK_CONSTANTS.CD_TRANSFER
    and upper(psr.si_status) = PFCS_PCK_CONSTANTS.SI_STATUS_ACTIVE
    and upper(psr.si_intent) = PFCS_PCK_CONSTANTS.SI_INTENT_PLAN
    and cur_sec.cd_estabelecimento = cd_establishment_p;

BEGIN
for c01_w in c01 loop
    qt_register_w := qt_register_w + 1;

    select  nextval('pfcs_panel_detail_seq')
    into STRICT  pfcs_panel_detail_seq_w
;

    insert into pfcs_panel_detail(
        nr_sequencia,
        nm_usuario,
        dt_atualizacao,
        nm_usuario_nrec,
        dt_atualizacao_nrec,
        ie_situation,
        nr_seq_indicator,
        nr_seq_operational_level)
    values (
        pfcs_panel_detail_seq_w,
        nm_user_p,
        clock_timestamp(),
        nm_user_p,
        clock_timestamp(),
        'T',
        nr_seq_indicator_w,
        cd_establishment_p);

    insert into pfcs_detail_bed(
        nr_sequencia,
        nm_usuario,
        dt_atualizacao,
        nm_usuario_nrec,
        dt_atualizacao_nrec,
        nr_seq_detail,
        ds_location,
        ds_request_unit,
        cd_department,
        ds_department,
        cd_status)
    values (
        nextval('pfcs_detail_bed_seq'),
        nm_user_p,
        clock_timestamp(),
        nm_user_p,
        clock_timestamp(),
        pfcs_panel_detail_seq_w,
        c01_w.ds_current_location,
        c01_w.ds_requested_location,
        c01_w.cd_unit,
        c01_w.ds_department,
        c01_w.cd_status);

    insert into pfcs_detail_patient(
        nr_sequencia,
        nm_usuario,
        dt_atualizacao,
        nm_usuario_nrec,
        dt_atualizacao_nrec,
        nr_seq_detail,
        nr_encounter_varchar,
        dt_entrance,
        id_patient,
        nm_patient,
        ds_gender,
        dt_birthdate,
        ds_age_range,
        ds_dnr_status,
        ds_special_request,
        ds_physician,
        ds_symptoms,
        dt_request,
        qt_edi_score,
        ie_freq_flyer,
        ds_care_status,
        ds_checklist,
        ds_readmission_risk,
        ds_readm_risk_contrb,
        ds_rec_pat_adm_data,
        ds_rec_pat_comorbd,
        ds_rec_pat_reasons,
		ds_edi_vitals_warn,
        ds_edi_contrb)
    values (
        nextval('pfcs_detail_patient_seq'),
        nm_user_p,
        clock_timestamp(),
        nm_user_p,
        clock_timestamp(),
        pfcs_panel_detail_seq_w,
        c01_w.nr_encounter,
        c01_w.dt_period_start,
        c01_w.id_patient,
        c01_w.nm_patient,
        c01_w.ds_gender,
        c01_w.dt_birthdate,
        c01_w.qt_patient_age,
        c01_w.cd_code_status,
        c01_w.ds_special_requests,
        c01_w.nm_practioner,
        c01_w.ds_problem,
        c01_w.dt_request,
        c01_w.qt_trs,
        c01_w.ie_frequent_flyer,
        c01_w.ds_care_status,
        c01_w.ds_checklist,
        c01_w.ds_readmission_risk,
        c01_w.ds_readm_risk_contrb,
        c01_w.ds_recur_pat_adm_data,
        c01_w.ds_recur_pat_comorbd,
        c01_w.ds_recur_pat_reasons,
		c01_w.ds_edi_vitals_warn,
        c01_w.ds_edi_contrb);
end loop;


 := pfcs_pck.pfcs_generate_results(
    vl_indicator_p              => qt_register_w, nr_seq_indicator_p          => nr_seq_indicator_w, nr_seq_operational_level_p  => cd_establishment_p, nm_usuario_p                => nm_user_p, nr_seq_panel_p              => pfcs_panel_seq_w);

CALL pfcs_pck.pfcs_update_detail(
    nr_seq_indicator_p          => nr_seq_indicator_w,
    nr_seq_panel_p              => pfcs_panel_seq_w,
    nr_seq_operational_level_p  => cd_establishment_p,
    nm_usuario_p                => nm_user_p);

CALL pfcs_pck.pfcs_activate_records(
    nr_seq_indicator_p          => nr_seq_indicator_w,
    nr_seq_operational_level_p  => cd_establishment_p,
    nm_usuario_p                => nm_user_p);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pfcs_pa_admits_to_or ( cd_establishment_p bigint, nm_user_p text) FROM PUBLIC;

