CREATE OR REPLACE PROCEDURE pfcs_pa_avg_time_ed_or ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


-- Variables
nr_seq_indicator_w        pfcs_panel.nr_seq_indicator%type := 159;
pfcs_panel_seq_w          pfcs_panel.nr_sequencia%type;

qt_patient_w    numeric(20) := 0;
qt_time_w       numeric(20) := 0;


BEGIN

select count(enc.nr_sequencia) nr_patient,
    coalesce(sum((psr.dt_authored_on - plh.dt_period_start) * 24 * 60 ), 0) total_time
into STRICT qt_patient_w,
    qt_time_w
from pfcs_encounter enc,
    pfcs_service_request psr,
    pfcs_patient pat,
    unidade_atendimento uni,
    setor_atendimento sec,
    pfcs_patient_loc_hist plh,
    unidade_atendimento uni_old,
    setor_atendimento sec_old
where pat.nr_sequencia = enc.nr_seq_patient
    and enc.nr_sequencia = psr.nr_seq_encounter
    and psr.nr_seq_location = uni.nr_seq_location
    and uni.cd_setor_atendimento = sec.cd_setor_atendimento
    and ((psr.cd_service in ('E0401') and upper(enc.cd_admit_source) = 'EMD') or (psr.cd_service in ('E0402') or  enc.nr_sequencia = plh.nr_seq_encounter))
    and upper(psr.si_intent) = 'ORDER'
    and upper(psr.si_status) = 'COMPLETED'
    and sec.cd_classif_setor = '2'
    and pat.ie_active = '1'
    and pat.nr_sequencia = plh.nr_seq_patient
    and plh.nr_seq_location = uni_old.nr_seq_location  
    and uni_old.cd_setor_atendimento = sec_old.cd_setor_atendimento
    and psr.dt_authored_on >= plh.dt_period_start
    and sec_old.cd_classif_setor = '1'
    and sec.cd_estabelecimento = cd_estabelecimento_p;


 := pfcs_pck.pfcs_generate_results(
    vl_indicator_p => qt_time_w, vl_indicator_aux_p => qt_patient_w, nr_seq_indicator_p => nr_seq_indicator_w, nr_seq_operational_level_p => cd_estabelecimento_p, nm_usuario_p => nm_usuario_p, nr_seq_panel_p => pfcs_panel_seq_w);

CALL pfcs_pck.pfcs_activate_records(
    nr_seq_indicator_p => nr_seq_indicator_w,
    nr_seq_operational_level_p => cd_estabelecimento_p,
    nm_usuario_p => nm_usuario_p);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
