SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pfcs_pa_calc_observ_hold ( cd_establishment_p bigint, nm_user_p text) AS $body$
DECLARE


nr_seq_indicator_w          pfcs_panel.nr_seq_indicator%type := 118;
pfcs_panel_detail_seq_w     pfcs_panel_detail.nr_sequencia%type;
pfcs_panel_seq_w            pfcs_panel.nr_sequencia%type;

qt_register_w               numeric(20) := 0;
qt_obsenc_time_w              numeric(20) := 0;

c01 CURSOR FOR
SELECT pfcs_get_human_name(pat.nr_sequencia, 'Patient') nm_patient,
    pat.gender ds_gender,
    to_char(round(months_between(coalesce(pat.deceased_date, clock_timestamp()), pat.birthdate))) qt_patient_age,
    pat.birthdate dt_birthdate,
    pat.patient_id id_patient,
    pfcs_get_patient_diagnosis(enc.nr_sequencia) ds_diagnosis,
    pfcs_get_human_name(pfcs_get_practitioner_seq(enc.nr_sequencia, '405279007'), 'Practitioner') nm_practioner,
    pfcs_get_code_status(pat.nr_sequencia, enc.nr_sequencia, 'S') ds_code_status,
    sec.ds_setor_atendimento || '-' || uni.cd_unidade_basica || '-' || uni.cd_unidade_compl ds_location,
    pfcs_get_special_requests(enc.nr_sequencia, uni.nr_seq_location,'E') ds_special_request,
    substr(obter_valor_dominio(82, uni.ie_status_unidade),1,255) ds_status,
    uni.ie_status_unidade cd_status,
    pfcs_get_edi_score(enc.nr_sequencia) nr_edi_score,
	coalesce(pfcs_get_checklist(enc.nr_sequencia, pat.nr_sequencia, 'MPL'),'') ds_care_status,
	coalesce(pfcs_get_checklist(enc.nr_sequencia, pat.nr_sequencia, 'TOOLTIP'),'') ds_checklist,
    pfcs_get_frequent_flyer(enc.nr_sequencia) ie_flag_recur_pat,
    pfcs_get_lace_plus(enc.nr_sequencia, enc.nr_seq_organization) ds_readmission_risk,
    pfcs_get_recurring_patient(pat.nr_sequencia) ds_recur_pat,
    enc.id_encounter nr_encounter_varchar,
    enc.period_start dt_period_start,
	pfcs_pck_utils.get_start_encounter_class(enc.nr_sequencia,PFCS_PCK_CONSTANTS.SI_CLASSIF_OBSENC) dt_obsenc_start,
    sec.ds_setor_atendimento ds_department,
    uni.ie_temporario ie_temporary,
	pfcs_obs_contributor_pck.get_laceplus(enc.nr_sequencia) ds_readm_risk_contrb,
	pfcs_obs_contributor_pck.get_freqflyer_visits(enc.nr_sequencia) ds_recur_pat_adm_data,
	pfcs_obs_contributor_pck.get_freqflyer_comorbidities(enc.nr_sequencia) ds_recur_pat_comorbd,
	pfcs_obs_contributor_pck.get_freqflyer_visit_reason(enc.nr_sequencia) ds_recur_pat_reasons,
	pfcs_obs_contributor_pck.get_edi_vital_warnings(enc.nr_sequencia) ds_edi_vitals_warn,
	pfcs_obs_contributor_pck.get_edi_contributors(enc.nr_sequencia) ds_edi_contrb,
    psr.dt_authored_on dt_svc_request
from pfcs_service_request psr,
    pfcs_encounter enc,
    pfcs_patient pat,
    unidade_atendimento uni,
    setor_atendimento sec
where enc.nr_seq_patient = pat.nr_sequencia
    and psr.nr_seq_patient = pat.nr_sequencia
    and psr.nr_seq_encounter = enc.nr_sequencia
    and uni.cd_setor_atendimento = sec.cd_setor_atendimento
    and sec.cd_classif_setor = '1' -- ED
    and sec.ie_situacao = 'A'
    and pat.ie_active = '1'
    and upper(enc.si_classif) = 'OBSENC' -- observation encounter
    and (enc.period_start IS NOT NULL AND enc.period_start::text <> '')
    and uni.nr_seq_location = coalesce(enc.nr_seq_location, pfcs_get_pat_location(pat.nr_sequencia, enc.nr_sequencia))
	and ((upper(psr.cd_service) in ('E0401', 'E0402')
                and upper(psr.si_status) = 'COMPLETED'
                and upper(psr.si_intent) = 'ORDER') or (upper(psr.cd_service) in ('E0402', 'E0405')
                and upper(psr.si_status) = 'ACTIVE'
                and upper(psr.si_intent) = 'PLAN'))
    and sec.cd_estabelecimento_base = cd_establishment_p;

BEGIN

for c01_w in c01 loop
    qt_register_w := qt_register_w + 1;
	qt_obsenc_time_w := qt_obsenc_time_w + coalesce((clock_timestamp() - c01_w.dt_obsenc_start),0);

    select  nextval('pfcs_panel_detail_seq')
    into STRICT  pfcs_panel_detail_seq_w
;

    insert into pfcs_panel_detail(
        nr_sequencia,
        nm_usuario,
        dt_atualizacao,
        nm_usuario_nrec,
        dt_atualizacao_nrec,
        ie_situation,
        nr_seq_indicator,
        nr_seq_operational_level)
    values (
        pfcs_panel_detail_seq_w,
        nm_user_p,
        clock_timestamp(),
        nm_user_p,
        clock_timestamp(),
        'T',
        nr_seq_indicator_w,
        cd_establishment_p);

    insert into pfcs_detail_bed(
        nr_sequencia,
        nm_usuario,
        dt_atualizacao,
        nm_usuario_nrec,
        dt_atualizacao_nrec,
        nr_seq_detail,
        ds_location,
        ds_department,
        ds_status,
        cd_status,
        ie_temporary)
    values (
        nextval('pfcs_detail_bed_seq'),
        nm_user_p,
        clock_timestamp(),
        nm_user_p,
        clock_timestamp(),
        pfcs_panel_detail_seq_w,
        c01_w.ds_location,
        c01_w.ds_department,
        c01_w.ds_status,
        c01_w.cd_status,
        c01_w.ie_temporary);

	insert into pfcs_detail_patient(
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_detail,
		nr_encounter_varchar,
		dt_entrance,
		id_patient,
		nm_patient,
		ds_gender,
		dt_birthdate,
		ds_age_range,
		ds_dnr_status,
		ds_special_request,
		ds_physician,
		ds_symptoms,
		qt_edi_score,
		ds_care_status,
		ds_checklist,
        dt_request,
		ds_readmission_risk,
		ie_freq_flyer,
		ds_recurring_patient,
		ds_readm_risk_contrb,
		ds_rec_pat_adm_data,
		ds_rec_pat_comorbd,
		ds_rec_pat_reasons,
		ds_edi_vitals_warn,
		ds_edi_contrb)
	values (
		nextval('pfcs_detail_patient_seq'),
		nm_user_p,
		clock_timestamp(),
		nm_user_p,
		clock_timestamp(),
		pfcs_panel_detail_seq_w,
		c01_w.nr_encounter_varchar,
		c01_w.dt_period_start,
		c01_w.id_patient,
		c01_w.nm_patient,
		c01_w.ds_gender,
		c01_w.dt_birthdate,
		c01_w.qt_patient_age,
		c01_w.ds_code_status,
		c01_w.ds_special_request,
		c01_w.nm_practioner,
		c01_w.ds_diagnosis,
		c01_w.nr_edi_score,
		c01_w.ds_care_status,
		c01_w.ds_checklist,
        c01_w.dt_svc_request,
		c01_w.ds_readmission_risk,
		c01_w.ie_flag_recur_pat,
		c01_w.ds_recur_pat,
		c01_w.ds_readm_risk_contrb,
		c01_w.ds_recur_pat_adm_data,
		c01_w.ds_recur_pat_comorbd,
		c01_w.ds_recur_pat_reasons,
		c01_w.ds_edi_vitals_warn,
		c01_w.ds_edi_contrb);
end loop;

 := pfcs_pck.pfcs_generate_results(
    vl_indicator_p              => qt_register_w, vl_indicator_aux_p          => dividir(qt_obsenc_time_w,qt_register_w), nr_seq_indicator_p          => nr_seq_indicator_w, nr_seq_operational_level_p  => cd_establishment_p, nm_usuario_p                => nm_user_p, nr_seq_panel_p              => pfcs_panel_seq_w);

CALL pfcs_pck.pfcs_update_detail(
    nr_seq_indicator_p          => nr_seq_indicator_w,
    nr_seq_panel_p              => pfcs_panel_seq_w,
    nr_seq_operational_level_p  => cd_establishment_p,
    nm_usuario_p                => nm_user_p);

CALL pfcs_pck.pfcs_activate_records(
    nr_seq_indicator_p          => nr_seq_indicator_w,
    nr_seq_operational_level_p  => cd_establishment_p,
    nm_usuario_p                => nm_user_p);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pfcs_pa_calc_observ_hold ( cd_establishment_p bigint, nm_user_p text) FROM PUBLIC;

