/**
* Find all the cunsultants
*/
c01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_consultor
	from	com_canal_consultor
	where	ie_funcao_exec in ('CONSULT','COORD');
	
BEGIN

	for reg01 in c01 loop
	begin
		CALL phi_gerar_mod_func_consult(reg01.nr_seq_consultor, nm_usuario_p);
	end;
	end loop;

end;
