SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pix_payment_return_status (cd_devolucao_pix_p bigint, cd_movto_pix_p bigint, nm_usuario_p text) AS $body$
DECLARE

  ds_data_w text;
  ds_result_w text;

  cd_cobranca_pix_w pix_movimento.nr_seq_cobranca%type;

  nr_seq_transmissao_w pix_transmissao.nr_sequencia%type;


BEGIN

  select    max(nr_seq_cobranca)            
    into STRICT    cd_cobranca_pix_w
    from    pix_movimento
    where   nr_sequencia = cd_movto_pix_p;

  select nextval('pix_transmissao_seq')
    into STRICT nr_seq_transmissao_w
;

    insert into pix_transmissao(
        nr_sequencia,
        nr_seq_pix_cob,
        dt_transmissao,
        nm_usuario,
        ie_tipo_transmissao)
    values (
        nr_seq_transmissao_w,
        cd_cobranca_pix_w,
        clock_timestamp(),
        nm_usuario_p,
        'R'
    );

   ds_data_w := '{ "paymentReturnId" : ' || cd_devolucao_pix_p || ',' ||
                 ' "transactionId" : ' || nr_seq_transmissao_w || ', ' ||
                 ' "user" : "' || nm_usuario_p || '" ' ||
                '}';
				
   ds_result_w := bifrost.send_integration('pix.consultar.devolucao',
    'com.philips.tasy.integration.pix.api.paymenttransaction.business.PaymentReturnCallBack',
    ds_data_w , nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
