CREATE OR REPLACE PROCEDURE pls_aceitar_valores ( ds_lista_contas_p text, ie_tipo_valor_p text, ie_servico_material text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_contas_w	varchar(2000);
ie_status_w		varchar(1);
nr_pos_virgula_w		bigint;
nr_sequencia_w		bigint;
nr_seq_conta_proc_w	bigint;		
nr_seq_conta_mat_w	bigint;
		
/* 
IE_SERVICO_MATERIAL 
S = Servi√ßos 
M = Materiais 
*/
		 

BEGIN 
if (ds_lista_contas_p IS NOT NULL AND ds_lista_contas_p::text <> '') and (ie_tipo_valor_p IS NOT NULL AND ie_tipo_valor_p::text <> '') and (ie_servico_material IS NOT NULL AND ie_servico_material::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	ds_lista_contas_w := ds_lista_contas_p;
	 
	while(ds_lista_contas_w IS NOT NULL AND ds_lista_contas_w::text <> '') loop 
		begin 
		 
		nr_pos_virgula_w	:= position(',' in ds_lista_contas_w);
		 
		if (nr_pos_virgula_w > 0) then 
			begin			 
			nr_sequencia_w		:= (substr(ds_lista_contas_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_contas_w	:= substr(ds_lista_contas_w,nr_pos_virgula_w+1,length(ds_lista_contas_w));			
			end;
		else 
			begin 
			nr_sequencia_w		:= (ds_lista_contas_w)::numeric;
			ds_lista_contas_w	:= null;
			end;
		end if;
			 
		if (nr_sequencia_w > 0) then 
			begin			 
			select	max(ie_status) 
			into STRICT	ie_status_w 
			from	pls_conta 
			where	nr_sequencia = nr_sequencia_w;
 
			if (ie_status_w = 'P') then 
				begin			 
				if (ie_servico_material = 'S') then 
					begin				 
					nr_seq_conta_proc_w := nr_sequencia_w;
					nr_seq_conta_mat_w := 0;						
					end;
				elsif (ie_servico_material = 'M') then 
					begin					 
					nr_seq_conta_proc_w := 0;
					nr_seq_conta_mat_w := nr_sequencia_w;					
					end;
				end if;			
				 
				CALL pls_conta_aceitar_valor( 
					0, 
					nr_seq_conta_proc_w, 
					nr_seq_conta_mat_w, 
					'N', 
					ie_tipo_valor_p, 
					cd_estabelecimento_p, 
					'C', 
					nm_usuario_p, 
					null, 
					null);						
				end;
			end if;	
			end;
		end if;		
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
