CREATE OR REPLACE PROCEDURE pls_aceita_importacao_glosa ( nr_seq_motivo_glosa_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_importacao_ops_w		varchar(1);
ie_importacao_ops_ww		varchar(1);

ie_permite_alteracao_ops_w	varchar(1);
cd_motivo_tiss_w		varchar(5);


BEGIN

begin
select	coalesce(ie_importacao_ops,'N'),
	coalesce(ie_permite_alteracao_ops,'N'),
	cd_motivo_tiss
into STRICT	ie_importacao_ops_w,
	ie_permite_alteracao_ops_w,
	cd_motivo_tiss_w
from	tiss_motivo_glosa
where	nr_sequencia	= nr_seq_motivo_glosa_p;
exception
when others then
	ie_importacao_ops_w	  	:= null;
	ie_permite_alteracao_ops_w	:= null;
	cd_motivo_tiss_w		:= null;
end;

/*Diego OS 411540 - Não é possivel alterar a permissão de importação em campos definidos pelo TISS como sendo obrigatórios.*/

if (ie_permite_alteracao_ops_w = 'N') and (ie_importacao_ops_w	= 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265810,'motivo_tiss='||cd_motivo_tiss_w);
	--Mensagem: Não é possivel alterar a permissão da importação da glosa '||cd_motivo_tiss_w||' pois a mesma é necessária para a integridade do sistema!
end if;

if (ie_importacao_ops_w = 'S') then
	ie_importacao_ops_ww	:= 'N';
elsif (ie_importacao_ops_w = 'N') then
	ie_importacao_ops_ww	:= 'S';
end if;

update	tiss_motivo_glosa
set	ie_importacao_ops	= ie_importacao_ops_ww,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_motivo_glosa_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
