SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_acrescentar_valor_fixo ( nr_seq_repasse_vend_p bigint, nr_seq_comissao_p bigint, ie_repasse_comiss_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* 	ie_repasse_comiss_p
		RM = repasse mensal
		CM = comissão lote mensal
*/
nr_seq_regra_mensal_w	pls_repasse_mens.nr_seq_regra_mensal%type;
vl_fixo_w		pls_regra_adic_mensal.vl_fixo%type;
qt_repasses_w		integer;
qt_comissoes_w		integer;
vl_rateado_w		double precision;
vl_total_repasses_w	pls_repasse_mens.vl_repasse%type;
vl_total_comissoes_w	pls_comissao_beneficiario.vl_comissao_benef%type;
nr_seq_comis_benef_w	pls_comissao_beneficiario.nr_sequencia%type;
vl_comissao_benef_w	pls_comissao_beneficiario.vl_comissao_benef%type;
qt_itens_w		integer;
vl_total_itens_w	pls_comissao_benef_item.vl_comissao%type;

C01 CURSOR FOR
	SELECT	distinct nr_seq_regra_mensal,
		vl_fixo
	from	pls_repasse_mens	b,
		pls_regra_adic_mensal	a
	where	a.nr_sequencia = b.nr_seq_regra_mensal
	and	b.nr_seq_repasse = nr_seq_repasse_vend_p
	and	coalesce(a.vl_fixo,0) > 0;

C02 CURSOR FOR
	SELECT	distinct nr_seq_regra_mensal,
		vl_fixo
	from	pls_comissao_beneficiario	b,
		pls_regra_adic_mensal		a
	where	a.nr_sequencia = b.nr_seq_regra_mensal
	and	b.nr_seq_comissao = nr_seq_comissao_p
	and	coalesce(a.vl_fixo,0) > 0;

C03 CURSOR FOR
	SELECT	nr_sequencia,
		vl_comissao_benef
	from	pls_comissao_beneficiario
	where	nr_seq_comissao = nr_seq_comissao_p
	and	nr_seq_regra_mensal = nr_seq_regra_mensal_w;


BEGIN

if (ie_repasse_comiss_p = 'RM') then -- Repasse mensal
	open C01;
	loop
	fetch C01 into
		nr_seq_regra_mensal_w,
		vl_fixo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	count(1)
		into STRICT	qt_repasses_w
		from	pls_repasse_mens
		where	nr_seq_repasse = nr_seq_repasse_vend_p
		and	nr_seq_regra_mensal = nr_seq_regra_mensal_w;

		vl_rateado_w := vl_fixo_w / qt_repasses_w;

		update	pls_repasse_mens
		set	vl_repasse = vl_rateado_w
		where	nr_seq_repasse = nr_seq_repasse_vend_p
		and	nr_seq_regra_mensal = nr_seq_regra_mensal_w;

		select	sum(vl_repasse)
		into STRICT	vl_total_repasses_w
		from	pls_repasse_mens
		where	nr_seq_repasse = nr_seq_repasse_vend_p
		and	nr_seq_regra_mensal = nr_seq_regra_mensal_w;

		if (vl_total_repasses_w <> vl_fixo_w) then
			update	pls_repasse_mens
			set	vl_repasse = vl_repasse + (vl_fixo_w - vl_total_repasses_w)
			where	nr_sequencia = (SELECT	max(x.nr_sequencia)
						from	pls_repasse_mens	x
						where	x.nr_seq_repasse = nr_seq_repasse_vend_p
						and	x.nr_seq_regra_mensal = nr_seq_regra_mensal_w);
		end if;

		end;
	end loop;
	close C01;
elsif (ie_repasse_comiss_p = 'CM') then -- Comissão lote mensal
	open C02;
	loop
	fetch C02 into
		nr_seq_regra_mensal_w,
		vl_fixo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	count(1)
		into STRICT	qt_comissoes_w
		from	pls_comissao_beneficiario
		where	nr_seq_comissao = nr_seq_comissao_p
		and	nr_seq_regra_mensal = nr_seq_regra_mensal_w;

		vl_rateado_w := vl_fixo_w / qt_comissoes_w;

		update	pls_comissao_beneficiario
		set	vl_comissao_benef = vl_rateado_w,
			vl_lanc_adic_benef = 0
		where	nr_seq_comissao = nr_seq_comissao_p
		and	nr_seq_regra_mensal = nr_seq_regra_mensal_w;

		select	sum(vl_comissao_benef)
		into STRICT	vl_total_comissoes_w
		from	pls_comissao_beneficiario
		where	nr_seq_comissao = nr_seq_comissao_p
		and	nr_seq_regra_mensal = nr_seq_regra_mensal_w;

		if (vl_total_comissoes_w <> vl_fixo_w) then
			update	pls_comissao_beneficiario
			set	vl_comissao_benef = vl_comissao_benef + (vl_fixo_w - vl_total_comissoes_w)
			where	nr_sequencia = (SELECT	max(x.nr_sequencia)
						from	pls_comissao_beneficiario	x
						where	x.nr_seq_comissao = nr_seq_comissao_p
						and	x.nr_seq_regra_mensal = nr_seq_regra_mensal_w);
		end if;

		open C03;
		loop
		fetch C03 into
			nr_seq_comis_benef_w,
			vl_comissao_benef_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin
			select	count(1)
			into STRICT	qt_itens_w
			from	pls_comissao_benef_item
			where	nr_seq_comissao_benef = nr_seq_comis_benef_w;

			if (qt_itens_w > 0) then
				vl_rateado_w := vl_comissao_benef_w / qt_itens_w;

				update	pls_comissao_benef_item
				set	vl_comissao = vl_rateado_w
				where	nr_seq_comissao_benef = nr_seq_comis_benef_w;

				select	sum(vl_comissao)
				into STRICT	vl_total_itens_w
				from	pls_comissao_benef_item
				where	nr_seq_comissao_benef = nr_seq_comis_benef_w;

				if (vl_total_itens_w <> vl_comissao_benef_w) then
					update	pls_comissao_benef_item
					set	vl_comissao = vl_comissao + (vl_comissao_benef_w - vl_total_itens_w)
					where	nr_sequencia = (SELECT	max(x.nr_sequencia)
								from	pls_comissao_benef_item	x
								where	x.nr_seq_comissao_benef = nr_seq_comis_benef_w);
				end if;
			end if;
			end;
		end loop;
		close C03;
		end;
	end loop;
	close C02;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
