CREATE OR REPLACE PROCEDURE pls_adic_cta_filtro_adic_fat ( nr_seq_lote_p pls_lote_faturamento.nr_sequencia%type, nr_seq_fatura_p pls_fatura.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


C01 CURSOR(	nr_seq_lote_pc		pls_lote_faturamento.nr_sequencia%type,
		nr_seq_fatura_pc	pls_fatura.nr_sequencia%type) FOR
	SELECT	c.nr_seq_conta,
		f.nr_seq_lote
	from	pls_fatura		f,
		pls_fatura_evento	e,
		pls_fatura_conta	c
	where	f.nr_sequencia	= e.nr_seq_fatura
	and	e.nr_sequencia	= c.nr_seq_fatura_evento
	and	f.nr_seq_lote	= nr_seq_lote_pc
	and	coalesce(nr_seq_fatura_pc::text, '') = ''
	
union

	SELECT	c.nr_seq_conta,
		f.nr_seq_lote
	from	pls_fatura		f,
		pls_fatura_evento	e,
		pls_fatura_conta	c
	where	f.nr_sequencia	= e.nr_seq_fatura
	and	e.nr_sequencia	= c.nr_seq_fatura_evento
	and	f.nr_sequencia	= nr_seq_fatura_pc
	and	coalesce(nr_seq_lote_pc::text, '') = '';

BEGIN
for r_c01_w in c01( nr_seq_lote_p , nr_seq_fatura_p ) loop
	insert into pls_lote_fat_adic(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
