	values (nextval('pls_lote_fat_adic_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		r_c01_w.nr_seq_lote,
		null,
		null,
		null,
		r_c01_w.nr_seq_conta,
		null,
		null);
