SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_agt_consistir_atv_vig_mat ( nr_seq_lote_guia_aut_p bigint, nr_seq_lote_mat_aut_p bigint, nr_seq_guia_plano_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Consistir se o 'Material, inativo ou fora de vigência'.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_material_w		varchar(10);
ie_mat_ativo_w		varchar(1);
nr_seq_material_w	bigint;
dt_autorizacao_w	timestamp;


BEGIN

begin
	select	dt_autorizacao
	into STRICT	dt_autorizacao_w
	from	pls_lote_anexo_guias_aut
	where	nr_sequencia	= nr_seq_lote_guia_aut_p;
exception
when others then
	dt_autorizacao_w	:= clock_timestamp();
end;

select	max(cd_material)
into STRICT	cd_material_w
from	pls_lote_anexo_mat_aut
where	nr_sequencia		= nr_seq_lote_mat_aut_p;

if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_material_w
	from	pls_material
	where	cd_material_ops	= cd_material_w;

	ie_mat_ativo_w := pls_obter_se_mat_ativo(nr_seq_material_w, dt_autorizacao_w);

	/* cmgross - 29/01/14 / N = O material não está ativo. / D = O material está fora da data de vigência. */

	if (ie_mat_ativo_w in ('N','D')) then
		CALL pls_inserir_glosa_anexo_guia('9920', nr_seq_lote_guia_aut_p, null, nr_seq_lote_mat_aut_p, nm_usuario_p);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_agt_consistir_atv_vig_mat ( nr_seq_lote_guia_aut_p bigint, nr_seq_lote_mat_aut_p bigint, nr_seq_guia_plano_p bigint, nm_usuario_p text) FROM PUBLIC;

