CREATE OR REPLACE PROCEDURE pls_agt_consistir_guia_mat ( nr_seq_lote_guia_aut_p bigint, nr_seq_guia_plano_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Consistir se o 'Material, inativo ou fora de vigência'e Consistir se o 'Material informado não coberto'.
-------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_lote_mat_aut_w	bigint;
qt_registros_glosa_w	bigint;
ie_status_w		smallint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_lote_anexo_mat_aut
	where	nr_seq_lote_anexo_guia	= nr_seq_lote_guia_aut_p;


BEGIN
open c01;
loop
fetch c01 into	nr_seq_lote_mat_aut_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	/* Consistir a glosa '2006' */

	CALL pls_agt_consistir_cobert_mat(nr_seq_lote_guia_aut_p, nr_seq_lote_mat_aut_w, nr_seq_guia_plano_p, nm_usuario_p);

	/* Consistir a glosa '9920' por material */

	CALL pls_agt_consistir_atv_vig_mat(nr_seq_lote_guia_aut_p, nr_seq_lote_mat_aut_w, nr_seq_guia_plano_p, nm_usuario_p);
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
