CREATE OR REPLACE PROCEDURE pls_agt_consistir_guia_proc ( nr_seq_lote_guia_aut_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 	Consistir se o 'Credenciado não é habilitado a realizar o procedimento'. 
	Consistir se o 'Procedimento é incompatível com o sexo do Beneficiário'. 
	Consistir se a 'Idade do Beneficiário é incompatível com o Procedimento'. 
	Consistir se a 'Quantidade de procedimento é maior que zero'. 
	Consistir se o 'Procedimento não autorizado'. 
	Consistir se o 'Procedimento ou material, inativo ou fora de vigência'. 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: Performance. 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
nr_seq_lote_proc_aut_w	bigint;
nr_seq_guia_plano_w	bigint;
qt_registros_glosa_w	bigint;
ie_status_w		smallint;
					
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_lote_anexo_proc_aut 
	where	nr_seq_lote_anexo_guia	= nr_seq_lote_guia_aut_p;
	

BEGIN 
select	nr_seq_guia 
into STRICT	nr_seq_guia_plano_w 
from	pls_lote_anexo_guias_aut 
where	nr_sequencia			= nr_seq_lote_guia_aut_p;
	 
open c01;
loop 
fetch c01 into	nr_seq_lote_proc_aut_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	/* Consistir a glosa '1214' */
 
	CALL pls_agt_consistir_proced_prest(nr_seq_lote_guia_aut_p, nr_seq_lote_proc_aut_w, nr_seq_guia_plano_w, nm_usuario_p);
	 
	/* Consistir a glosa '1802' e '1803' */
 
	CALL pls_agt_consistir_idade_sexo(nr_seq_lote_guia_aut_p, nr_seq_lote_proc_aut_w, nr_seq_guia_plano_w, nm_usuario_p);
	 
	/* Consistir a glosa '1806' */
	 
	CALL pls_agt_consistir_quant_proc(nr_seq_lote_guia_aut_p, nr_seq_lote_proc_aut_w, nr_seq_guia_plano_w, nm_usuario_p);
	 
	/* Consistir a glosa '1815' */
 
	CALL pls_agt_consistir_acomo_diaria(nr_seq_lote_guia_aut_p, nr_seq_lote_proc_aut_w, nr_seq_guia_plano_w, nm_usuario_p);
	 
	/* Consistir a glosa '9920' por procedimeto */
 
	CALL pls_agt_consistir_atv_vig_proc(nr_seq_lote_guia_aut_p, nr_seq_lote_proc_aut_w, nr_seq_guia_plano_w, nm_usuario_p);
end loop;
close c01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
