CREATE OR REPLACE PROCEDURE pls_ajustar_campos_repasse ( nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_repasse_w		bigint;
			
C01 CURSOR FOR 
	SELECT	nr_sequencia nr_seq_repasse 
	from	pls_repasse_mens 
	where	coalesce(nm_segurado::text, '') = '' 
	and	coalesce(nr_parcela_contrato::text, '') = '';
	
TYPE 		fetch_array IS TABLE OF c01%ROWTYPE;
s_array 	fetch_array;
i		integer := 1;
type Vetor is table of fetch_array index by integer;
Vetor_c01_w			Vetor;

BEGIN 
 
open c01;
loop 
FETCH C01 BULK COLLECT INTO s_array LIMIT 1000;
	Vetor_c01_w(i) := s_array;
	i := i + 1;
EXIT WHEN NOT FOUND; /* apply on C01 */
END LOOP;
CLOSE C01;
 
for i in 1..Vetor_c01_w.COUNT loop 
	s_array := Vetor_c01_w(i);
	for z in 1..s_array.COUNT loop 
		begin 
		 
		nr_seq_repasse_w		:= s_array[z].nr_seq_repasse;
		 
		CALL pls_atual_campos_repasse_mens(nr_seq_repasse_w,nm_usuario_p);
		 
		end;
	end loop;
end loop;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
