SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_carencias_seg ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_carencia_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	pls_carencia	b
	where	not exists (	SELECT	1
					from	pls_plano	a
					where	b.nr_seq_plano		= a.nr_sequencia
					and	a.ie_tipo_operacao	= 'A')
	and	coalesce(b.ie_origem_carencia_benef::text, '') = '';

C02 CURSOR FOR
	SELECT	b.nr_sequencia
	from	pls_carencia	b,
		pls_plano	a
	where	b.nr_seq_plano		= a.nr_sequencia
	and	a.ie_tipo_operacao	= 'A'
	and	(b.nr_seq_plano IS NOT NULL AND b.nr_seq_plano::text <> '')
	and	coalesce(b.ie_origem_carencia_benef::text, '') = '';



BEGIN

/*Carência não vinculadas as SCA's*/

open C01;
loop
fetch C01 into
	nr_seq_carencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_carencia
	set	ie_origem_carencia_benef	= 'P',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_carencia_w;

	end;
end loop;
close C01;

/*Carência vinculadas as SCA's*/

open C02;
loop
fetch C02 into
	nr_seq_carencia_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	update	pls_carencia
	set	ie_origem_carencia_benef	= 'S',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_carencia_w;

	end;
end loop;
close C02;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
