SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_carencia_benef_mig ( nm_usuario_p text ) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
nr_seq_segurado_ant_w		bigint;
nr_seq_plano_ant_w		bigint;
nr_seq_contrato_ant_w		bigint;
nr_seq_carencia_w		bigint;
qt_carencias_ant_plano_w	bigint;
qt_carencias_ant_contrato_w	bigint;
nr_seq_tipo_carencia_w		bigint;
dt_inclusao_operadora_w		timestamp;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_segurado	a
	where	(a.nr_seq_segurado_ant IS NOT NULL AND a.nr_seq_segurado_ant::text <> '')
	and	exists (	SELECT	1
				from	pls_carencia	x
				where	x.nr_seq_segurado	= a.nr_sequencia
                                and	(x.ds_observacao IS NOT NULL AND x.ds_observacao::text <> '')
				and	(x.dt_inicio_vigencia IS NOT NULL AND x.dt_inicio_vigencia::text <> ''));

C02 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_tipo_carencia
	from	pls_carencia
	where	nr_seq_segurado	= nr_seq_segurado_w
	and	trunc(dt_inicio_vigencia,'dd') <> trunc(dt_inclusao_operadora_w,'dd')
	and	coalesce(nr_seq_regra_aut::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nr_seq_segurado_ant,
		dt_inclusao_operadora
	into STRICT	nr_seq_segurado_ant_w,
		dt_inclusao_operadora_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_w;

	begin
	select	nr_seq_plano,
		nr_seq_contrato
	into STRICT	nr_seq_plano_ant_w,
		nr_seq_contrato_ant_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_ant_w;
	exception
	when others then
		nr_seq_plano_ant_w	:= null;
		nr_seq_contrato_ant_w	:= null;
	end;

	if (nr_seq_plano_ant_w IS NOT NULL AND nr_seq_plano_ant_w::text <> '') and (nr_seq_contrato_ant_w IS NOT NULL AND nr_seq_contrato_ant_w::text <> '') then
		open C02;
		loop
		fetch C02 into
			nr_seq_carencia_w,
			nr_seq_tipo_carencia_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			select	count(*)
			into STRICT	qt_carencias_ant_contrato_w
			from	pls_carencia
			where	nr_seq_contrato		= nr_seq_contrato_ant_w
			and	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_w;

			if (qt_carencias_ant_contrato_w > 0) then
				update	pls_carencia
				set	ds_observacao		= ds_observacao|| chr(10) || ' Alteração de data início vigente de : '|| to_char(dt_inicio_vigencia,'dd/mm/yyyy')
								|| ' para: ' || to_char(dt_inclusao_operadora_w,'dd/mm/yyyy') || '-> Contrato',
					dt_inicio_vigencia	= dt_inclusao_operadora_w,
					dt_fim_vigencia		= dt_inclusao_operadora_w + qt_dias,
					nm_usuario		= nm_usuario_p,
					dt_atualizacao		= clock_timestamp()
				where	nr_sequencia		= nr_seq_carencia_w
				and	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_w;

			else
				select	count(*)
				into STRICT	qt_carencias_ant_plano_w
				from	pls_carencia
				where	nr_seq_plano		= nr_seq_plano_ant_w
				and	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_w;

				if (qt_carencias_ant_plano_w > 0) then
					update	pls_carencia
					set	ds_observacao		= ds_observacao|| chr(10) || ' Alteração de data início vigente de : '|| to_char(dt_inicio_vigencia,'dd/mm/yyyy')
									|| ' para: ' || to_char(dt_inclusao_operadora_w,'dd/mm/yyyy')|| '-> Produto',
						dt_inicio_vigencia	= dt_inclusao_operadora_w,
						dt_fim_vigencia		= dt_inclusao_operadora_w + qt_dias,
						nm_usuario		= nm_usuario_p,
						dt_atualizacao		= clock_timestamp()
					where	nr_sequencia		= nr_seq_carencia_w
					and	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_w;
				end if;
			end if;
			end;
		end loop;
		close C02;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ajustar_carencia_benef_mig ( nm_usuario_p text ) FROM PUBLIC;

