CREATE OR REPLACE PROCEDURE pls_ajustar_cooperat_contrato () AS $body$
DECLARE


nr_seq_congenere_w	bigint;
nr_seq_contrato_w	bigint;
cd_cooperativa_w	varchar(10);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_contrato;



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(nr_seq_congenere),0)
	into STRICT	nr_seq_congenere_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;

	if (nr_seq_congenere_w <> 0) then
		select	max(cd_cooperativa)
		into STRICT	cd_cooperativa_w
		from	pls_congenere
		where	nr_sequencia	= nr_seq_congenere_w;

		update	pls_contrato
		set	cd_cooperativa	= cd_cooperativa_w
		where	nr_sequencia	= nr_seq_contrato_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
