SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_data_aux_reajuste () AS $body$
DECLARE


nr_seq_reajuste_w		bigint;
dt_reajuste_w			timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_reajuste
	from	pls_reajuste
	where	coalesce(dt_reajuste_aux::text, '') = '';



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_reajuste_w,
	dt_reajuste_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_reajuste
	set	dt_reajuste_aux	= to_char(dt_reajuste_w,'mm/yyyy')
	where	nr_sequencia	= nr_seq_reajuste_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
