CREATE OR REPLACE PROCEDURE pls_ajustar_documentos_pf ( cd_estabelecimento_p bigint) AS $body$
DECLARE



nr_seq_tipo_documento_w		bigint;
cd_pessoa_fisica_w		varchar(20);
nr_seq_tipo_doc_w		bigint; --sequÃªncia da tabela pls_tipo_documento_pf
