nr_seq_documento_w		bigint;
nr_seq_doc_tipo_w		bigint;
cd_estabelecimento_w		smallint;

C01 CURSOR FOR
	SELECT	a.nr_seq_tipo_documento,
		a.cd_pessoa_fisica,
		a.dt_atualizacao,
		a.nm_usuario,
		coalesce(a.dt_atualizacao_nrec,clock_timestamp()),
		a.nm_usuario_nrec,
		a.nr_sequencia
	from	pls_documento_pf	a,
		pls_segurado		b
	where	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
	and	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	coalesce(a.nr_seq_tipo_doc_pf::text, '') = ''
	and	(a.nr_seq_tipo_documento IS NOT NULL AND a.nr_seq_tipo_documento::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_tipo_documento_w,
