	dt_atualizao_nrec_w,
	nm_usuario_nrec_w,
	nr_seq_documento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_sequencia)
	into STRICT	nr_seq_doc_tipo_w
	from	pls_tipo_documento_pf
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w
	and	nr_seq_tipo_documento	= nr_seq_tipo_documento_w;

	if (coalesce(nr_seq_doc_tipo_w,0) = 0) then

		select	max(cd_estabelecimento)
		into STRICT	cd_estabelecimento_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w;

		select	nextval('pls_tipo_documento_pf_seq')
		into STRICT	nr_seq_tipo_doc_w
		;

		insert into pls_tipo_documento_pf(	nr_sequencia, cd_estabelecimento , dt_atualizacao ,
				nm_usuario, dt_atualizacao_nrec , nm_usuario_nrec,
				nr_seq_tipo_documento, dt_registro , nm_usuario_registro,
				cd_pessoa_fisica	)
		values (	nr_seq_tipo_doc_w, coalesce(cd_estabelecimento_w,cd_estabelecimento_p) , dt_atualizao_w,
				nm_usuario_w , dt_atualizao_nrec_w, nm_usuario_nrec_w,
				nr_seq_tipo_documento_w , dt_atualizao_w , nm_usuario_w,
				cd_pessoa_fisica_w	);

		update	pls_documento_pf
		set	nr_seq_tipo_doc_pf	= nr_seq_tipo_doc_w
		where	nr_sequencia		= nr_seq_documento_w;
	else
		update	pls_documento_pf
		set	nr_seq_tipo_doc_pf	= nr_seq_doc_tipo_w
		where	nr_sequencia		= nr_seq_documento_w;

