SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_dt_ocorrencia_sib () AS $body$
DECLARE


nr_seq_historico_w	bigint;
ie_w			bigint;
dt_historico_w		timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_historico
	from	pls_segurado_historico
	where	IE_TIPO_HISTORICO	in (14,15,16,19)
	and	coalesce(dt_ocorrencia_sib::text, '') = '';


BEGIN

ie_w	:= 0;

open C01;
loop
fetch C01 into
	nr_seq_historico_w,
	dt_historico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ie_w	:= ie_w + 1;

	update	pls_segurado_historico
	set	dt_ocorrencia_sib	= dt_historico_w
	where	nr_sequencia		= nr_seq_historico_w;

	if (ie_w = 100) then
		ie_w	:= 0;
		commit;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ajustar_dt_ocorrencia_sib () FROM PUBLIC;

