SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_empresa_seg () AS $body$
DECLARE


nr_seq_segurado_w		bigint;
nr_seq_contr_inter_w		bigint;
ie_tipo_vinculo_w		varchar(1);
cd_operadora_empresa_w		varchar(10);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_contrato,
		'C'
	from	pls_segurado
	where	coalesce(cd_operadora_empresa::text, '') = ''
	and	(nr_seq_contrato IS NOT NULL AND nr_seq_contrato::text <> '')
	
union

	SELECT	nr_sequencia,
		nr_seq_intercambio,
		'I'
	from	pls_segurado
	where	coalesce(cd_operadora_empresa::text, '') = ''
	and	(nr_seq_intercambio IS NOT NULL AND nr_seq_intercambio::text <> '');



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_segurado_w,
	nr_seq_contr_inter_w,
	ie_tipo_vinculo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_tipo_vinculo_w = 'C') then
		begin
		select	max(cd_operadora_empresa)
		into STRICT	cd_operadora_empresa_w
		from	pls_contrato
		where	nr_sequencia	= nr_seq_contr_inter_w;
		exception
		when others then
			cd_operadora_empresa_w	:= null;
		end;
	elsif (ie_tipo_vinculo_w = 'I') then
		begin
		select	max(cd_operadora_empresa)
		into STRICT	cd_operadora_empresa_w
		from	pls_intercambio
		where	nr_sequencia	= nr_seq_contr_inter_w;
		exception
		when others then
			cd_operadora_empresa_w	:= null;
		end;
	end if;

	if (cd_operadora_empresa_w IS NOT NULL AND cd_operadora_empresa_w::text <> '') then
		update	pls_segurado
		set	cd_operadora_empresa	= cd_operadora_empresa_w
		where	nr_sequencia		= nr_seq_segurado_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
