CREATE OR REPLACE PROCEDURE pls_ajustar_escala_vendedores ( nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_solic_atend_w		bigint;
nr_seq_origem_solic_w		bigint;
qt_registros_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_solicitacao_regra_aten
	where	cd_estabelecimento	= cd_estabelecimento_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_solic_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_acao_origem)
	into STRICT	nr_seq_origem_solic_w
	from	pls_solicitacao_regra_aten
	where	nr_sequencia	= nr_seq_solic_atend_w;

	if (nr_seq_origem_solic_w IS NOT NULL AND nr_seq_origem_solic_w::text <> '') then
		select	count(*)
		into STRICT	qt_registros_w
		from	pls_solic_regra_escala
		where	nr_seq_regra		= nr_seq_solic_atend_w
		and	nr_seq_origem_solic	= nr_seq_origem_solic_w;

		if (qt_registros_w = 0) then
			insert into pls_solic_regra_escala(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_regra,nr_seq_origem_solic,cd_estabelecimento)
			values (	nextval('pls_solic_regra_escala_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_solic_atend_w,nr_seq_origem_solic_w,cd_estabelecimento_p);
		end if;
	end if;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
