SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_estab_carteiras () AS $body$
DECLARE


nr_seq_carteira_w	bigint;
cd_estabelecimento_w	bigint;
cd_estab_plano_w	bigint;

C01 CURSOR FOR
	SELECT	a.cd_estabelecimento,
		c.nr_sequencia,
		d.cd_estabelecimento
	FROM pls_segurado_carteira c, pls_segurado b
LEFT OUTER JOIN pls_contrato a ON (b.nr_seq_contrato = a.nr_sequencia)
LEFT OUTER JOIN pls_plano d ON (b.nr_seq_plano = d.nr_sequencia)
WHERE c.nr_seq_segurado	= b.nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	cd_estabelecimento_w,
	nr_seq_carteira_w,
	cd_estab_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_segurado_carteira
	set	cd_estabelecimento	= coalesce(cd_estabelecimento_w,cd_estab_plano_w)
	where	nr_sequencia		= nr_seq_carteira_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
