CREATE OR REPLACE PROCEDURE pls_ajustar_fat_proc_pos_estab () AS $body$
DECLARE


nr_seq_conta_pos_w		bigint;
nr_seq_conta_proc_w		bigint;
nr_seq_fat_proc_w		bigint;
nr_seq_conta_mat_w		bigint;
nr_seq_fat_mat_w		bigint;


C01 CURSOR FOR
	SELECT	a.nr_seq_conta_proc,
		a.nr_sequencia
	from	pls_fatura_proc a;

C02 CURSOR FOR
	SELECT	a.nr_seq_conta_mat,
		a.nr_sequencia
	from	pls_fatura_mat a;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_conta_proc_w,
	nr_seq_fat_proc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_conta_pos_w
	from	pls_conta_pos_estabelecido a
	where	a.nr_seq_conta_proc 	= nr_seq_conta_proc_w
	and	((a.ie_situacao		= 'A') or (coalesce(a.ie_situacao::text, '') = ''));

	update	pls_fatura_proc
	set	nr_seq_conta_pos_estab 	= nr_seq_conta_pos_w
	where	nr_sequencia		= nr_seq_fat_proc_w;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_conta_mat_w,
	nr_seq_fat_mat_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_conta_pos_w
	from	pls_conta_pos_estabelecido a
	where	a.nr_seq_conta_mat 	= nr_seq_conta_mat_w
	and	((a.ie_situacao		= 'A') or (coalesce(a.ie_situacao::text, '') = ''));

	update	pls_fatura_mat
	set	nr_seq_conta_pos_estab 	= nr_seq_conta_pos_w
	where	nr_sequencia		= nr_seq_fat_mat_w;
	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
