CREATE OR REPLACE PROCEDURE pls_ajustar_lote_ref_reajuste ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_ref_w		bigint;
nr_seq_lote_reaju_seg_w		bigint;
nr_seq_reajuste_w		bigint;


C01 CURSOR FOR
	SELECT	x.nr_sequencia,
		a.NR_SEQ_LOTE_REFERENCIA
	from	pls_lote_reaj_segurado	x,
		pls_reajuste		a
	where	coalesce(x.nr_seq_lote_referencia::text, '') = ''
	and	a.nr_sequencia	= x.nr_seq_reajuste
	and	(a.nr_seq_lote_referencia IS NOT NULL AND a.nr_seq_lote_referencia::text <> '')
	and	a.ie_tipo_reajuste	= 'C';

C02 CURSOR FOR
	SELECT	x.nr_sequencia,
		x.nr_seq_reajuste
	from	pls_lote_reaj_segurado		x
	where	exists (	SELECT	1
				from	pls_reajuste	a
				where	a.nr_sequencia	= x.nr_seq_reajuste
				and	(a.nr_seq_lote_referencia IS NOT NULL AND a.nr_seq_lote_referencia::text <> ''))
	and	x.nr_seq_lote_referencia is  null;

C03 CURSOR FOR
	SELECT	x.nr_sequencia,
		x.nr_seq_reajuste
	from	pls_lote_reaj_segurado		x
	where	exists (	SELECT	1
				from	pls_reajuste	a
				where	a.nr_seq_lote_referencia	= x.nr_seq_reajuste
				and	(a.nr_seq_lote_referencia IS NOT NULL AND a.nr_seq_lote_referencia::text <> ''))
	and	x.nr_seq_lote_referencia is  null;



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_lote_reaju_seg_w,
	nr_seq_lote_ref_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_lote_reaj_segurado
	set	nr_seq_lote_referencia	= nr_seq_lote_ref_w
	where	nr_sequencia		= nr_seq_lote_reaju_seg_w;

	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_lote_reaju_seg_w,
	nr_seq_reajuste_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	select	nr_seq_lote_referencia
	into STRICT	nr_seq_lote_ref_w
	from	pls_reajuste
	where	nr_sequencia	= nr_seq_reajuste_w;

	update	pls_lote_reaj_segurado
	set	nr_seq_lote_referencia	= nr_seq_lote_ref_w
	where	nr_sequencia		= nr_seq_lote_reaju_seg_w;

	end;
end loop;
close C02;

open C03;
loop
fetch C03 into
	nr_seq_lote_reaju_seg_w,
	nr_seq_lote_ref_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin

	update	pls_lote_reaj_segurado
	set	nr_seq_lote_referencia	= nr_seq_lote_ref_w
	where	nr_sequencia		= nr_seq_lote_reaju_seg_w;

	end;
end loop;
close C03;

commit;

end;
$body$
