SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_mens_dt_cobertura () AS $body$
DECLARE


nr_seq_mensalidade_seq_w	bigint;
dt_mesano_referencia_w		timestamp;
dt_contratacao_w		timestamp;
nr_parcela_w			bigint;
ie_mensalidade_proporcional_w	varchar(1);
dt_inicio_cobertura_w		timestamp;
dt_fim_cobertura_w		timestamp;

C01 CURSOR FOR
	SELECT	c.nr_sequencia,
		c.dt_mesano_referencia,
		d.dt_contratacao,
		c.nr_parcela,
		d.ie_mensalidade_proporcional
	from	pls_segurado d,
		pls_mensalidade_segurado c,
		pls_mensalidade b,
		pls_lote_mensalidade a
	where	c.nr_seq_segurado = d.nr_sequencia
	and	b.nr_sequencia	= c.nr_seq_mensalidade
	and	a.nr_sequencia	= b.nr_seq_lote
	and	trunc(a.dt_mesano_referencia,'month')	>= to_date('01/12/2012');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_mensalidade_seq_w,
	dt_mesano_referencia_w,
	dt_contratacao_w,
	nr_parcela_w,
	ie_mensalidade_proporcional_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_parcela_w = 1) and (coalesce(ie_mensalidade_proporcional_w,'N') = 'S') then
		dt_inicio_cobertura_w	:= trunc(dt_contratacao_w,'dd');
		dt_fim_cobertura_w	:= trunc(last_day(dt_contratacao_w),'dd');
	elsif (nr_parcela_w = 1) and (coalesce(ie_mensalidade_proporcional_w,'N') = 'N') then
		dt_inicio_cobertura_w	:= trunc(dt_contratacao_w,'dd');
		dt_fim_cobertura_w	:= trunc(add_months(dt_contratacao_w,1),'dd');
	elsif (nr_parcela_w > 1) then
		if (ie_mensalidade_proporcional_w = 'S') then
			dt_inicio_cobertura_w	:= trunc(dt_mesano_referencia_w,'month');
			dt_fim_cobertura_w	:= last_day(dt_mesano_referencia_w);
		else
			begin
			dt_inicio_cobertura_w	:= to_date(to_char(dt_contratacao_w,'dd') || '/'|| to_char(dt_mesano_referencia_w,'mm/yyyy'));
			exception
			when others then
				dt_inicio_cobertura_w	:= last_day(dt_mesano_referencia_w);
			end;

			dt_fim_cobertura_w	:= add_months(dt_inicio_cobertura_w,1);
		end if;
	end if;

	update	pls_mensalidade_segurado
	set	dt_inicio_cobertura	= dt_inicio_cobertura_w,
		dt_fim_cobertura	= dt_fim_cobertura_w
	where	nr_sequencia	= nr_seq_mensalidade_seq_w;

	commit;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
