CREATE OR REPLACE PROCEDURE pls_ajustar_nomes_sib ( nr_seq_lote_sib_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);
nm_beneficiario_w	varchar(255);
nm_mae_benef_w		varchar(255);
i_w			bigint := 0;
ds_endereco_w		varchar(50);
ds_bairro_w		varchar(30);
ds_complemento_w	varchar(15);
nr_seq_segurado_w	pls_interf_sib.nr_seq_segurado%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nm_mae_benef,
		nm_beficiario,
		logradouro,
		ds_bairro,
		ds_complemento,
		nr_seq_segurado
	from	pls_interf_sib
	where	nr_seq_lote_sib = nr_seq_lote_sib_p
	and	ie_tipo_reg in (1,2);


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nm_mae_benef_w,
	nm_beneficiario_w,
	ds_endereco_w,
	ds_bairro_w,
	ds_complemento_w,
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	i_w	:= i_w + 1;

	nm_mae_benef_w	:= elimina_acentos(nm_mae_benef_w);

	nm_beneficiario_w	:= elimina_acentos(nm_beneficiario_w);

	nm_mae_benef_w		:= substr(nm_mae_benef_w,1,59);
	nm_beneficiario_w	:= substr(nm_beneficiario_w,1,69);

	ds_endereco_w		:= elimina_acentos(ds_endereco_w);
	ds_bairro_w		:= elimina_acentos(ds_bairro_w);
	ds_complemento_w	:= elimina_acentos(ds_complemento_w);

	nm_mae_benef_w		:= replace(replace(replace(replace(replace(replace(replace(replace(replace(replace(nm_mae_benef_w,'>',''),'<',''),'.',''),'ç','c'),'Ç','C'),chr(39),''),'|',''),'¦',''),'Ï',''),chr(9),'');
	nm_beneficiario_w	:= replace(replace(replace(replace(replace(replace(replace(replace(replace(replace(nm_beneficiario_w,'>',''),'<',''),'.',''),'ç','c'),'Ç','C'),chr(39),''),'|',''),'¦',''),'Ï',''),chr(9),'');
	ds_endereco_w		:= replace(ds_endereco_w,'.','');
	ds_bairro_w		:= replace(ds_bairro_w,'.','');
	ds_complemento_w	:= replace(ds_complemento_w,'.','');

	update	pls_interf_sib
	set	nm_beficiario	= rtrim(nm_beneficiario_w),
		nm_mae_benef	= rtrim(nm_mae_benef_w),
		logradouro	= ds_endereco_w,
		ds_bairro	= ds_bairro_w,
		ds_complemento	= ds_complemento_w
	where	nr_seq_lote_sib = nr_seq_lote_sib_p
	and	nr_sequencia	= nr_sequencia_w
	and	nr_seq_segurado = nr_seq_segurado_w;

	if (i_w  = 100) then
		i_w := 0;
		commit;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
