SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_nr_contr_via_adic () AS $body$
DECLARE


nr_seq_lote_w		bigint;
nr_seq_contrato_w	bigint;
nr_contrato_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_contrato
	from	pls_via_adic_cart_lote
	where	coalesce(nr_contrato::text, '') = ''
	and	(nr_seq_contrato IS NOT NULL AND nr_seq_contrato::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_lote_w,
	nr_seq_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nr_contrato
	into STRICT	nr_contrato_w
	from	pls_contrato
	where	nr_sequencia  	= nr_seq_contrato_w;

	update	pls_via_adic_cart_lote
	set 	nr_contrato	= nr_contrato_w
	where	nr_sequencia	= nr_seq_lote_w;

	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
