CREATE OR REPLACE PROCEDURE pls_ajustar_operacao_carencia () AS $body$
DECLARE


nr_seq_tipo_carencia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_tipo_carencia
	where	coalesce(ie_tipo_operacao::text, '') = '';


BEGIN


open C01;
loop
fetch C01 into
	nr_seq_tipo_carencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_tipo_carencia
	set	ie_tipo_operacao	= 'P'
	where	nr_sequencia		= nr_seq_tipo_carencia_w
	and	coalesce(ie_tipo_operacao::text, '') = '';

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
