CREATE OR REPLACE PROCEDURE pls_ajustar_pagador_fat_titulo () AS $body$
DECLARE


nr_titulo_w		bigint;
nr_titulo_ndc_w		bigint;
nr_seq_pagador_w	bigint;

c01 CURSOR FOR
	SELECT	nr_titulo,
		nr_titulo_ndc,
		nr_seq_pagador
	from	pls_fatura;


BEGIN
open C01;
loop
fetch C01 into
	nr_titulo_w,
	nr_titulo_ndc_w,
	nr_seq_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then
		update	titulo_receber
		set	nr_seq_pagador	= nr_seq_pagador_w
		where	nr_titulo	= nr_titulo_w
		and	coalesce(nr_seq_pagador::text, '') = '';

	elsif (nr_titulo_ndc_w IS NOT NULL AND nr_titulo_ndc_w::text <> '') then
		update	titulo_receber
		set	nr_seq_pagador	= nr_seq_pagador_w
		where	nr_titulo	= nr_titulo_ndc_w
		and	coalesce(nr_seq_pagador::text, '') = '';

	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
