SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_param_comercial () AS $body$
DECLARE


qt_registros_22_w		bigint;
qt_registros_21_w		bigint;


BEGIN

delete	from FUNCAO_PARAM_HIST
where	nr_seq_param	in (21, 22)
and	cd_funcao	= 1237;

/* Excluir o parâmetro 22 */

select	count(*)
into STRICT	qt_registros_22_w
from	funcao_parametro
where	cd_funcao	= 1237
and	nr_sequencia	= 22;

if (qt_registros_22_w > 0) then
	begin
	delete	from funcao_parametro
	where	cd_funcao	= 1237
	and	nr_sequencia	= 22;
	exception
	when others then
		update	funcao_parametro
		set	ds_parametro	= 'Inativo'
		where	cd_funcao	= 1237
		and	nr_sequencia	= 22;
	end;
end if;

/* Excluir o parâmetro 21 */

select	count(*)
into STRICT	qt_registros_21_w
from	funcao_parametro
where	cd_funcao	= 1237
and	nr_sequencia	= 21;

if (qt_registros_22_w > 0) then
	begin
	delete	from funcao_parametro
	where	cd_funcao	= 1237
	and	nr_sequencia	= 21;
	exception
	when others then
		update	funcao_parametro
		set	ds_parametro	= 'Inativo'
		where	cd_funcao	= 1237
		and	nr_sequencia	= 21;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
