CREATE OR REPLACE PROCEDURE pls_ajustar_pessoa_matricula ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_controle_cart_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_carteira_controle_iden
	where	ie_tipo_regra	= 'F'
	and	ie_tipo_pessoa	= 'PF';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_controle_cart_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_carteira_controle_iden
	set	ie_tipo_pessoa	= 'A',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_controle_cart_w;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
