SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_preco_benef () AS $body$
DECLARE


nr_seq_segurado_w		bigint;
nr_seq_tabela_w			bigint;
nr_seq_plano_preco_w		bigint;
vl_anterior_w			double precision;
vl_preco_inicial_w		double precision;
nr_seq_reaj_preco_w		bigint;
nr_seq_segurado_preco_w		bigint;
vl_anterior_ww			double precision;
vl_preco_inicial_ww		double precision;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_tabela
	from	pls_segurado a
	where	a.nr_seq_contrato in (3511,3508,3507,3500,3499,3495,3492,3491,3489,3486,3480,3472,3470,3468,3462,3459,3446,3443,3418,3415,3414,3392,3371,3297,3296,3294,3293);

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_plano_preco
	where	nr_seq_tabela	= nr_seq_tabela_w;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_segurado_w,
	nr_seq_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_plano_preco_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	round((a.vl_preco_inicial/1.0676)::numeric,2),
			vl_preco_inicial
		into STRICT	vl_anterior_w,
			vl_preco_inicial_w
		from	pls_plano_preco a
		where	a.nr_sequencia	= nr_seq_plano_preco_w;

		select	max(nr_sequencia)
		into STRICT	nr_seq_reaj_preco_w
		from	pls_reajuste_preco
		where	nr_seq_preco	= nr_seq_plano_preco_w
		and	trunc(dt_reajuste,'dd')	= to_date('01/01/2010');

		if (coalesce(nr_seq_reaj_preco_w,0) <> 0) then
			update	pls_reajuste_preco
			set	vl_base		= vl_anterior_w,
				vl_reajustado	= vl_preco_inicial_w
			where	nr_sequencia	= nr_seq_reaj_preco_w;
		end if;

		update	pls_plano_preco
		set	vl_preco_inicial	= vl_anterior_w,
			vl_preco_atual		= vl_preco_inicial_w
		where	nr_sequencia		= nr_seq_plano_preco_w;
		end;
	end loop;
	close C02;

	select	max(nr_sequencia)
	into STRICT	nr_seq_segurado_preco_w
	from	pls_segurado_preco
	where	nr_seq_segurado	= nr_seq_segurado_w
	and	trunc(dt_reajuste,'dd')	= to_date('01/01/2010');

	if (coalesce(nr_seq_segurado_preco_w,0) <> 0) then
		select	round((a.vl_preco_ant/1.0676)::numeric,2),
			vl_preco_ant
		into STRICT	vl_anterior_ww,
			vl_preco_inicial_ww
		from	pls_segurado_preco a
		where	a.nr_sequencia	= nr_seq_segurado_preco_w;

		update	pls_segurado_preco
		set	vl_preco_ant	= vl_anterior_ww,
			vl_preco_atual	= vl_preco_inicial_ww,
			vl_desconto	= coalesce(vl_desconto,0)
		where	nr_sequencia	= nr_seq_segurado_preco_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
