CREATE OR REPLACE PROCEDURE pls_ajustar_proc_prop_apres ( nr_seq_conta_proc_p bigint, vl_apres_co_p INOUT bigint, vl_apres_hi_p INOUT bigint, vl_apres_material_p INOUT bigint, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE


vl_apres_w			double precision;
vl_apres_co_w			double precision;
vl_apres_hi_w			double precision;
vl_apres_material_w		double precision;

ie_repassa_medico_w		varchar(1);

vl_calculado_w			double precision;
vl_calculado_hi_w		double precision;
vl_calculado_co_w		double precision;
vl_calculado_material_w		double precision;

vl_base_w			double precision;
vl_liberado_w			double precision;

vl_lib_w			double precision;

BEGIN

begin
select	ie_repassa_medico,
	vl_calc_hi_util,
	vl_calc_co_util,
	vl_calc_mat_util,
	vl_procedimento,
	vl_procedimento_imp
into STRICT	ie_repassa_medico_w,
	vl_calculado_hi_w,
	vl_calculado_co_w,
	vl_calculado_material_w,
	vl_calculado_w,
	vl_apres_w
from	pls_conta_proc
where	nr_sequencia = nr_seq_conta_proc_p;
exception
when others then
	vl_calculado_hi_w	 := 0;
	vl_calculado_co_w	 := 0;
	vl_calculado_material_w	 := 0;
	vl_calculado_w		 := 0;
end;

for i in 1..3 loop
	begin

	if (i = 1) then
		vl_base_w := vl_calculado_hi_w;
	elsif (i = 2) then
		vl_base_w := vl_calculado_co_w;
	else
		vl_base_w := vl_calculado_material_w;
	end if;

	vl_lib_w := dividir_sem_round((vl_apres_w * vl_base_w), vl_calculado_w);

	if (i = 1) then
		vl_apres_hi_w 		:= vl_lib_w;
	elsif (i = 2) then
		vl_apres_co_w 		:= vl_lib_w;
	else
		vl_apres_material_w 	:= vl_lib_w;
	end if;

	end;
end loop;

vl_apres_co_p		:= vl_apres_co_w;
vl_apres_hi_p		:= vl_apres_hi_w;
vl_apres_material_p	:= vl_apres_material_w;

end;
$body$
