CREATE OR REPLACE PROCEDURE pls_ajustar_prod_contratos_sbc ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w	bigint;
nr_seq_plano_w		bigint;
nr_seq_tabela_w		bigint;
nr_seq_contrato_w	bigint;
qt_registros_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_sequencia
	from	pls_contrato	b,
		pls_segurado	a
	where	a.nr_seq_contrato	= b.nr_sequencia
	and	not exists (	SELECT	1
				from	pls_contrato_plano	x
				where	a.nr_seq_contrato	= b.nr_sequencia
				and	a.nr_seq_plano		= x.nr_seq_plano
				and	a.nr_seq_tabela		= x.nr_seq_tabela);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_segurado_w,
	nr_seq_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nr_seq_plano,
		nr_seq_tabela
	into STRICT	nr_seq_plano_w,
		nr_seq_tabela_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_w;

	if (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') and (nr_seq_tabela_w IS NOT NULL AND nr_seq_tabela_w::text <> '') then

		select	count(*)
		into STRICT	qt_registros_w
		from	pls_contrato_plano
		where	nr_seq_contrato	= nr_seq_contrato_w
		and	nr_seq_plano	= nr_seq_plano_w
		and	nr_seq_tabela	= nr_seq_tabela_w;

		if (qt_registros_w = 0) then
			insert into pls_contrato_plano(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_contrato,nr_seq_plano,nr_seq_tabela,ie_situacao,ie_tipo_operacao)
			values (	nextval('pls_contrato_plano_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_contrato_w,nr_seq_plano_w,nr_seq_tabela_w,'A','B');
		end if;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
